import React from 'react';
import { motion } from 'framer-motion';
import { FaUserGraduate, FaFolderOpen } from 'react-icons/fa';
import { Link } from 'react-scroll';

const About = () => {
  return (
    <section id="about" className="px-6 py-16 text-white bg-dark">
      {/* Heading */}
      <motion.h2
        initial={{ opacity: 0, y: -20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        viewport={{ once: true }}
        className="text-4xl font-bold text-center mb-12 text-primary"
      >
        About Me
      </motion.h2>

      {/* Cards */}
      <div className="grid md:grid-cols-2 gap-8 max-w-6xl mx-auto">
        {/* Card 1 */}
        <motion.div
          className="bg-gray-900 p-6 rounded-lg shadow-md flex items-center gap-6"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
          whileHover={{ scale: 1.05 }}
        >
          <FaUserGraduate className="text-4xl text-primary" />
          <div>
            <h3 className="text-xl font-semibold">Final Year Student</h3>
            <p className="text-gray-400">Currently pursuing B.Tech in Computer Science.</p>
          </div>
        </motion.div>

        {/* Card 2 */}
        <motion.div
          className="bg-gray-900 p-6 rounded-lg shadow-md flex items-center gap-6 justify-between"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          viewport={{ once: true }}
          whileHover={{ scale: 1.05 }}
        >
          <FaFolderOpen className="text-4xl text-primary" />
          <div className="flex-1">
            <h3 className="text-xl font-semibold">Projects Completed</h3>
            <p className="text-gray-400">10+ successfully built and deployed projects.</p>
          </div>
          <Link
            to="projects"
            smooth={true}
            duration={500}
            className="bg-primary text-white px-4 py-2 rounded hover:bg-opacity-80 cursor-pointer"
          >
            View Projects
          </Link>
        </motion.div>
      </div>

      {/* Description */}
      <motion.div
        className="max-w-4xl mx-auto mt-12 text-lg text-gray-300 text-center"
        initial={{ opacity: 0, y: 20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6, delay: 0.4 }}
        viewport={{ once: true }}
      >
        <p>
          {/* Fill this paragraph with your detailed about content */}
          Passionate about software development, problem-solving, and always eager to learn new technologies and contribute to meaningful projects.
        </p>
      </motion.div>
    </section>
  );
};

export default About;
