import React from 'react';

const About = () => {
  return (
    <section id="about" className="text-white py-16 px-8">
      <div className="max-w-4xl mx-auto text-center">
        <h2 className="text-4xl font-bold mb-4 text-primary">About Me</h2>
        <p className="text-gray-300 text-lg leading-relaxed">
          I'm Kommana Roshitha, a passionate Computer Science student with strong interests in web development,
          artificial intelligence, and UI/UX design. I enjoy turning complex problems into simple, beautiful, and
          intuitive designs.
        </p>
      </div>
    </section>
  );
};

export default About;
