import React, { useState, useRef } from 'react';
import { motion } from 'framer-motion';
import { FaEnvelope, FaLinkedin, FaWhatsapp } from 'react-icons/fa';
import emailjs from '@emailjs/browser';

const Contact = () => {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [showPopup, setShowPopup] = useState(false);
  const formRef = useRef();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    emailjs.sendForm(
      'service_ln1b367',
      'template_wgcza8i',
      formRef.current,
      'T-hfPbdDWJ4vL7fyq'
    )
    .then(() => {
      setShowPopup(true);
      setFormData({ name: '', email: '', message: '' });
    })
    .catch((err) => {
      console.error('FAILED...', err);
    });
  };

  const handlePopupClose = () => {
    setShowPopup(false);
  };

  return (
    <section id="contact" className="px-6 py-20 text-gray-900 bg-transparent">
      <div className="max-w-6xl mx-auto grid md:grid-cols-2 gap-12">
        {/* Left: Form */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
          className="bg-white/40 backdrop-blur-md p-8 rounded-xl border border-gray-200 shadow-md"
        >
          <h2 className="text-3xl font-bold mb-6 text-purple-700">Contact Me</h2>
          <form ref={formRef} onSubmit={handleSubmit} className="space-y-4">
            <input
              type="text"
              name="name"
              placeholder="Your Full Name"
              required
              className="w-full p-3 rounded bg-white border border-gray-300 text-black placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500"
              value={formData.name}
              onChange={handleChange}
            />
            <input
              type="email"
              name="email"
              placeholder="Your Email"
              required
              className="w-full p-3 rounded bg-white border border-gray-300 text-black placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500"
              value={formData.email}
              onChange={handleChange}
            />
            <textarea
              name="message"
              rows="5"
              placeholder="Your Message"
              required
              className="w-full p-3 rounded bg-white border border-gray-300 text-black placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500"
              value={formData.message}
              onChange={handleChange}
            ></textarea>
            <button
              type="submit"
              className="bg-purple-600 text-white px-6 py-3 rounded shadow hover:scale-105 transition-transform"
            >
              Send
            </button>
          </form>
        </motion.div>

        {/* Right: Contact Cards */}
        <motion.div
          className="flex flex-col gap-6"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          viewport={{ once: true }}
        >
          {[
            {
              icon: <FaEnvelope className="text-purple-600 text-3xl" />,
              title: 'Email',
              value: 'roshithakommana18@gmail.com',
              link: 'mailto:roshithakommana18@gmail.com',
            },
            {
              icon: <FaLinkedin className="text-purple-600 text-3xl" />,
              title: 'LinkedIn',
              value: 'linkedin.com/in/roshitha-kommana',
              link: 'https://linkedin.com/in/roshitha-kommana',
            },
            {
              icon: <FaWhatsapp className="text-purple-600 text-3xl" />,
              title: 'WhatsApp',
              value: '+91 63026 80957',
              link: 'https://wa.me/916302680957',
            },
          ].map((item, idx) => (
            <motion.div
              key={idx}
              whileHover={{ scale: 1.05 }}
              className="bg-white/40 backdrop-blur-md p-6 rounded-lg border border-gray-200 shadow-md flex items-center gap-4 transition"
            >
              {item.icon}
              <div>
                <h4 className="text-lg font-semibold text-gray-900">{item.title}</h4>
                <a href={item.link} target="_blank" rel="noopener noreferrer" className="text-gray-700 hover:text-purple-600">
                  {item.value}
                </a>
              </div>
            </motion.div>
          ))}
        </motion.div>
      </div>

      {/* Popup */}
      {showPopup && (
        <div className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-50 z-50">
          <div className="backdrop-blur-md bg-white/20 text-white p-8 rounded-xl shadow-lg border border-white/30">
            <h3 className="text-xl font-semibold mb-4">Sent Successfully!</h3>
            <button
              onClick={handlePopupClose}
              className="bg-purple-600 px-4 py-2 rounded hover:bg-purple-700"
            >
              OK
            </button>
          </div>
        </div>
      )}
    </section>
  );
};

export default Contact;
