import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { FolderKanban, BadgeCheck } from 'lucide-react';

const projects = [/* your existing project data */];

const certificates = [
  { id: 1, imageUrl: '/certificates/leadership.jpg', link: 'https://www.credly.com/badges/xxxxxxxx' },
  { id: 2, imageUrl: '/certificates/cloudcomputing.jpg', link: '' },
  { id: 3, imageUrl: '/assets/certificate3.png', link: '' },
  { id: 4, imageUrl: '/assets/certificate4.png', link: '' },
  { id: 5, imageUrl: '/assets/certificate5.png', link: '' },
];

const badges = [
  { id: 1, imageUrl: '/badges/aws.jpg', link: 'https://www.credly.com/badges/aws-badge-url' },
  { id: 2, imageUrl: '/badges/ibm.jpg', link: 'https://www.credly.com/badges/ibm-badge-url' },
  { id: 3, imageUrl: '/badges/coursera.jpg', link: 'https://www.credly.com/badges/coursera-badge-url' },
  { id: 4, imageUrl: '/badges/cybersecurity.png', link: 'https://www.credly.com/badges/cisco-cybersecurity-essentials-id' },
];

const CertificatesCarousel = ({ certificates }) => {
  const [selected, setSelected] = useState(null);
  const [startIndex, setStartIndex] = useState(0);
  const itemsPerPage = 3;

  const visibleItems = certificates.slice(startIndex, startIndex + itemsPerPage);

  const nextSlide = () => {
    if (startIndex + itemsPerPage < certificates.length) {
      setStartIndex(startIndex + 1);
    }
  };

  const prevSlide = () => {
    if (startIndex > 0) {
      setStartIndex(startIndex - 1);
    }
  };

  return (
    <div className="relative w-full flex flex-col items-center">
      <div className="flex justify-center items-center space-x-4">
        {startIndex > 0 && (
          <button onClick={prevSlide} className="bg-purple-300 hover:bg-purple-500 text-white rounded-full p-1 text-sm">«</button>
        )}

        <div className="flex space-x-6 transition-transform duration-700 ease-out">
          {visibleItems.map((item) => (
            <div
              key={item.id}
              className="relative w-64 h-52 flex-shrink-0 group bg-[#0b0f19] border border-[#3f3f3f] rounded-lg px-4 py-2"
            >
              <div className="w-full h-full flex items-center justify-center">
                <img
                  src={item.imageUrl}
                  alt={`Certificate ${item.id}`}
                  className="max-w-[100%] max-h-[100%] object-contain rounded-md"
                />
              </div>
              <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-60 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                <button onClick={() => window.open(item.link || '#', '_blank')} className="text-white text-lg font-semibold hover:underline transition">
                  View Certificate
                </button>
              </div>
            </div>
          ))}
        </div>

        {startIndex + itemsPerPage < certificates.length && (
          <button onClick={nextSlide} className="bg-purple-300 hover:bg-purple-500 text-white rounded-full p-1 text-sm">»</button>
        )}
      </div>
    </div>
  );
};

const BadgesCarousel = ({ badges }) => {
  const [startIndex, setStartIndex] = useState(0);
  const itemsPerPage = 3;

  const visibleBadges = badges.slice(startIndex, startIndex + itemsPerPage);

  const nextSlide = () => {
    if (startIndex + itemsPerPage < badges.length) {
      setStartIndex(startIndex + 1);
    }
  };

  const prevSlide = () => {
    if (startIndex > 0) {
      setStartIndex(startIndex - 1);
    }
  };

  return (
    <div className="relative w-full flex flex-col items-center">
      <div className="flex justify-center items-center space-x-4">
        {startIndex > 0 && (
          <button onClick={prevSlide} className="bg-purple-300 hover:bg-purple-500 text-white rounded-full p-1 text-sm">«</button>
        )}

        <div className="flex space-x-6 transition-transform duration-700 ease-out">
          {visibleBadges.map((badge) => (
            <div
              key={badge.id}
              className="relative w-[220px] h-[220px] flex-shrink-0 group bg-[#0b0f19] border border-[#3f3f3f] rounded-xl overflow-hidden"
            >
              <img
                src={badge.imageUrl}
                alt={`Badge ${badge.id}`}
                className="w-full h-full object-cover object-center"
              />
              <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-60 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                <button onClick={() => window.open(badge.link, '_blank')} className="text-white text-lg font-semibold hover:underline transition">
                  View Badge
                </button>
              </div>
            </div>
          ))}
        </div>

        {startIndex + itemsPerPage < badges.length && (
          <button onClick={nextSlide} className="bg-purple-300 hover:bg-purple-500 text-white rounded-full p-1 text-sm">»</button>
        )}
      </div>
    </div>
  );
};

const ProjectsCertificates = () => {
  const [activeTab, setActiveTab] = useState('projects');

  return (
    <section className="max-w-7xl mx-auto px-6 py-20 bg-[#0b0f19] text-white">
      <motion.h2
        className="text-4xl font-extrabold mb-12 text-center text-purple-400"
        initial={{ opacity: 0, y: -30 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        Projects & Certificates
      </motion.h2>

      <div className="flex justify-center space-x-6 mb-14">
        {[
          { key: 'projects', label: 'Projects', icon: <FolderKanban className="w-4 h-4 mr-2" /> },
          { key: 'certificates', label: 'Certificates', icon: <BadgeCheck className="w-4 h-4 mr-2" /> },
          { key: 'badges', label: 'Badges', icon: <BadgeCheck className="w-4 h-4 mr-2 text-yellow-400" /> },
        ].map((tab) => (
          <motion.button
            key={tab.key}
            onClick={() => setActiveTab(tab.key)}
            className={`px-6 py-2 rounded-xl font-semibold transition-all duration-300 ease-in-out flex items-center
              ${activeTab === tab.key
                ? 'bg-purple-700/80 border border-purple-400 text-white shadow-lg'
                : 'bg-purple-900/30 border border-purple-600 text-purple-300 hover:bg-purple-800/40'}`}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.97 }}
          >
            {tab.icon}
            <span>{tab.label}</span>
          </motion.button>
        ))}
      </div>

      {activeTab === 'certificates' ? (
        <motion.div initial={{ opacity: 0, x: 50 }} whileInView={{ opacity: 1, x: 0 }} transition={{ duration: 0.6 }}>
          <CertificatesCarousel certificates={certificates} />
        </motion.div>
      ) : activeTab === 'badges' ? (
        <motion.div initial={{ opacity: 0, x: 50 }} whileInView={{ opacity: 1, x: 0 }} transition={{ duration: 0.6 }}>
          <BadgesCarousel badges={badges} />
        </motion.div>
      ) : (
        <motion.div initial={{ opacity: 0, x: 50 }} whileInView={{ opacity: 1, x: 0 }} transition={{ duration: 0.6 }}>
          <div className="text-purple-300">Project Cards Here</div>
          {/* Add your project rendering logic */}
        </motion.div>
      )}
    </section>
  );
};

export default ProjectsCertificates;
