import React from 'react';
import { motion } from 'framer-motion';

import { DiCode } from 'react-icons/di';       // Languages
import { FaDatabase, FaCheckCircle } from 'react-icons/fa';  // Backend & Checkmarks
import { FiTool } from 'react-icons/fi';       // Tools & Libraries
import { MdLightbulb } from 'react-icons/md';  // Concepts
import { GiSkills } from 'react-icons/gi';     // Key Strengths icon

const skillsData = [
  {
    category: 'Languages',
    icon: <DiCode size={26} className="text-purple-500" />,
    skills: ['Java', 'Python', 'C++', 'C', 'HTML', 'CSS', 'JavaScript', 'Tailwind CSS', 'React.js'],
  },
  {
    category: 'Backend & Databases',
    icon: <FaDatabase size={26} className="text-purple-500" />,
    skills: ['Firebase', 'MongoDB', 'MySQL'],
  },
  {
    category: 'Tools & Libraries',
    icon: <FiTool size={26} className="text-purple-500" />,
    skills: ['OpenCV', 'Tesseract OCR', 'Git', 'VS Code'],
  },
  {
    category: 'Concepts',
    icon: <MdLightbulb size={26} className="text-purple-500" />,
    skills: ['OOP', 'DBMS', 'Data Structures', 'Machine Learning', 'Big Data', 'UI/UX Principles'],
  },
  {
    category: 'Key Strengths',
    icon: <GiSkills size={26} className="text-purple-500" />,
    skills: ['Problem Solving', 'Team Collaboration', 'Active Listener', 'Time Management'],
  },
];

const Skills = () => {
  return (
    <section id="skills" className="max-w-6xl mx-auto py-20 px-4 bg-[#0b0f19] text-white">
      <h2 className="text-4xl font-bold mb-16 text-center text-purple-400">Skills</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10">
        {skillsData.map(({ category, icon, skills }, index) => (
          <motion.div
            key={category}
            initial={{ opacity: 0, scale: 0.85 }}
            whileInView={{ opacity: 1, scale: 1 }}
            viewport={{ once: true, amount: 0.6 }}
            transition={{ duration: 0.5, delay: index * 0.2 }}
            className="bg-[#111827] rounded-xl p-6 shadow-md hover:shadow-purple-400/50 cursor-pointer"
          >
            <div className="flex items-center mb-5 space-x-3">
              {icon}
              <h3 className="text-2xl font-semibold text-purple-400">{category}</h3>
            </div>

            <div className="flex flex-wrap gap-3">
              {skills.map((skill) => (
                <span
                  key={skill}
                  className="flex items-center space-x-2 bg-purple-700/20 text-purple-300 px-4 py-1 rounded-full text-sm font-medium select-none"
                >
                  <FaCheckCircle className="text-purple-500" />
                  <span>{skill}</span>
                </span>
              ))}
            </div>
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default Skills;
