import React from 'react';
import { motion } from 'framer-motion';

import { DiCode } from 'react-icons/di';       // Languages
import { FaDatabase, FaCheckCircle } from 'react-icons/fa';  // Backend & Checkmarks
import { FiTool } from 'react-icons/fi';       // Tools & Libraries
import { MdLightbulb } from 'react-icons/md';  // Concepts
import { GiSkills } from 'react-icons/gi';     // Key Strengths icon

const skillsData = [
  {
    category: 'Languages',
    icon: <DiCode size={22} className="text-purple-500" />,
    skills: ['Java', 'Python', 'C++', 'C', 'HTML', 'CSS', 'JavaScript', 'Tailwind CSS', 'React.js'],
  },
  {
    category: 'Backend & Databases',
    icon: <FaDatabase size={22} className="text-purple-500" />,
    skills: ['Firebase', 'MongoDB', 'MySQL'],
  },
  {
    category: 'Tools & Libraries',
    icon: <FiTool size={22} className="text-purple-500" />,
    skills: ['OpenCV', 'Tesseract OCR', 'Git', 'VS Code'],
  },
  {
    category: 'Concepts',
    icon: <MdLightbulb size={22} className="text-purple-500" />,
    skills: ['OOP', 'DBMS', 'Data Structures', 'Machine Learning', 'Big Data', 'UI/UX Principles'],
  },
  {
    category: 'Key Strengths',
    icon: <GiSkills size={22} className="text-purple-500" />,
    skills: ['Problem Solving', 'Team Collaboration', 'Active Listener', 'Time Management'],
  },
];

// Animation variants
const floatAnimation = {
  initial: { y: 0 },
  animate: {
    y: [0, -8, 0], // float up and down
    transition: {
      duration: 4,
      repeat: Infinity,
      ease: 'easeInOut',
    },
  },
};

const cardHover = {
  scale: 1.05,
  boxShadow: '0 0 15px 3px rgba(124, 58, 237, 0.6)',
  transition: { duration: 0.3 },
};

const Skills = () => {
  return (
    <section
      id="skills"
      className="max-w-7xl mx-auto py-20 px-6 bg-[#0b0f19] text-white select-none"
    >
      <h2 className="text-4xl font-extrabold mb-16 text-center text-purple-400 tracking-wide">
        Skills & Strengths
      </h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-10">
        {skillsData.map(({ category, icon, skills }) => (
          <motion.div
            key={category}
            className="bg-[#1e2235] rounded-xl p-8 cursor-pointer border border-transparent hover:border-purple-600 transition-all"
            variants={floatAnimation}
            initial="initial"
            animate="animate"
            whileHover={cardHover}
          >
            <div className="flex items-center mb-6 space-x-4">
              <div className="p-3 bg-purple-600 rounded-full flex justify-center items-center text-white shadow-md">
                {icon}
              </div>
              <h3 className="text-2xl font-semibold text-purple-300 tracking-wide">
                {category}
              </h3>
            </div>

            <div className="flex flex-wrap gap-3">
              {skills.map((skill) => (
                <span
                  key={skill}
                  className="flex items-center space-x-2 bg-purple-800/30 text-purple-200 px-4 py-1 rounded-full text-sm font-medium select-text"
                >
                  <FaCheckCircle className="text-purple-400" />
                  <span>{skill}</span>
                </span>
              ))}
            </div>
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default Skills;
