import React from 'react';
import { motion } from 'framer-motion';

const educationData = [
  {
    id: 1,
    institute: 'Sri Chaitanya Techno School',
    course: '10th Grade',
    year: 'Completed in 2021',
    score: '100%',
  },
  {
    id: 2,
    institute: 'Sri Chaitanya Junior College',
    course: 'Intermediate',
    year: 'Completed in 2023',
    score: '74.5%',
  },
  {
    id: 3,
    institute: 'XYZ University',
    course: 'B.Tech in Computer Science (3rd Year)',
    year: '2021 - Present',
    score: 'CGPA 8.53',
  },
];

const Education = () => {
  return (
    <section id="education" className="text-white px-6 py-16 bg-dark max-w-6xl mx-auto">
      <motion.h2
        className="text-3xl font-bold mb-10 text-primary text-center"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        Education
      </motion.h2>

      <div className="flex flex-col gap-6 max-w-3xl mx-auto">
        {educationData.map(({ id, institute, course, year, score }) => (
          <motion.div
            key={id}
            className="bg-gray-800 rounded-lg p-6 shadow-md hover:shadow-xl transition-shadow cursor-default"
            initial={{ opacity: 0, x: -30 }}
            whileInView={{ opacity: 1, x: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.5, delay: id * 0.2 }}
          >
            <h3 className="text-xl font-semibold mb-1 text-primary">{course}</h3>
            <p className="text-gray-400 mb-1">{institute}</p>
            <p className="text-gray-500 italic mb-2">{year}</p>
            <p className="text-white font-medium">{score}</p>
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default Education;
