import React from 'react';
import { motion } from 'framer-motion';

import { DiCode } from 'react-icons/di';       // Languages
import { FaDatabase, FaCheckCircle } from 'react-icons/fa';  // Backend & Checkmarks
import { FiTool } from 'react-icons/fi';       // Tools & Libraries
import { MdLightbulb } from 'react-icons/md';  // Concepts
import { GiSkills } from 'react-icons/gi';     // Key Strengths icon

const skillsData = [
  {
    category: 'Languages',
    icon: <DiCode size={20} className="text-purple-500" />,
    skills: ['Java', 'Python', 'C++', 'C', 'HTML', 'CSS', 'JavaScript', 'Tailwind CSS', 'React.js'],
  },
  {
    category: 'Backend & Databases',
    icon: <FaDatabase size={20} className="text-purple-500" />,
    skills: ['Firebase', 'MongoDB', 'MySQL'],
  },
  {
    category: 'Tools & Libraries',
    icon: <FiTool size={20} className="text-purple-500" />,
    skills: ['OpenCV', 'Tesseract OCR', 'Git', 'VS Code'],
  },
  {
    category: 'Concepts',
    icon: <MdLightbulb size={20} className="text-purple-500" />,
    skills: ['OOP', 'DBMS', 'Data Structures', 'Machine Learning', 'Big Data', 'UI/UX Principles'],
  },
  {
    category: 'Key Strengths',
    icon: <GiSkills size={20} className="text-purple-500" />,
    skills: ['Problem Solving', 'Team Collaboration', 'Active Listener', 'Time Management'],
  },
];

// Animation Variants
const containerVariant = {
  hidden: {},
  visible: {
    transition: {
      staggerChildren: 0.12,
    },
  },
};

const cardVariant = {
  hidden: { opacity: 0, y: 20, scale: 0.9 },
  visible: { opacity: 1, y: 0, scale: 1, transition: { duration: 0.4, ease: 'easeOut' } },
  hover: { scale: 1.05, boxShadow: '0 8px 24px rgba(124, 58, 237, 0.5)' },
};

const skillVariant = {
  hidden: { opacity: 0, x: -10 },
  visible: { opacity: 1, x: 0, transition: { duration: 0.3 } },
};

const Skills = () => {
  return (
    <section id="skills" className="max-w-7xl mx-auto py-20 px-6 bg-[#0b0f19] text-white select-none">
      <h2 className="text-4xl font-extrabold mb-16 text-center text-purple-400 tracking-wide">
        Skills & Strengths
      </h2>

      <motion.div
        className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-10"
        variants={containerVariant}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true, amount: 0.4 }}
      >
        {skillsData.map(({ category, icon, skills }) => (
          <motion.div
            key={category}
            variants={cardVariant}
            whileHover="hover"
            className="bg-[#1e2235] rounded-xl p-8 shadow-md cursor-pointer border border-transparent hover:border-purple-600 transition-all"
          >
            <div className="flex items-center mb-6 space-x-4">
              <div className="p-3 bg-purple-600 rounded-full flex justify-center items-center text-white shadow-md">
                {icon}
              </div>
              <h3 className="text-2xl font-semibold text-purple-300 tracking-wide">{category}</h3>
            </div>

            <motion.div className="flex flex-wrap gap-3" variants={containerVariant}>
              {skills.map((skill) => (
                <motion.span
                  key={skill}
                  variants={skillVariant}
                  className="flex items-center space-x-2 bg-purple-800/40 text-purple-200 px-4 py-1 rounded-full text-sm font-medium shadow-sm"
                >
                  <FaCheckCircle className="text-purple-400" />
                  <span>{skill}</span>
                </motion.span>
              ))}
            </motion.div>
          </motion.div>
        ))}
      </motion.div>
    </section>
  );
};

export default Skills;
