import React, { useState, useRef } from 'react';
import { motion } from 'framer-motion';
import {
  FaEnvelope,
  FaWhatsapp,
  FaPaperPlane,
  FaGithub,
  FaLinkedin,
  FaInstagram,
} from 'react-icons/fa';
import emailjs from '@emailjs/browser';

const Contact = () => {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [showPopup, setShowPopup] = useState(false);
  const formRef = useRef();

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    emailjs.sendForm(
      'service_ln1b367',
      'template_wgcza8i',
      formRef.current,
      'T-hfPbdDWJ4vL7fyq'
    )
    .then(() => {
      setShowPopup(true);
      setFormData({ name: '', email: '', message: '' });
    })
    .catch((err) => console.error('FAILED...', err));
  };

  const handlePopupClose = () => setShowPopup(false);

  return (
    <section id="contact" className="px-6 py-20 bg-transparent text-gray-900">
      <motion.h2
        className="text-4xl font-bold text-center text-black mb-16"
        initial={{ opacity: 0, y: -20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        Contact Me
      </motion.h2>

      <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-12">
        {/* Left: Get in Touch Form */}
        <motion.div
          className="bg-white/20 backdrop-blur-md p-8 rounded-2xl shadow-md border border-gray-200"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <h3 className="text-2xl font-semibold text-black-700 mb-4">Get in Touch with Me</h3>
          <p className="text-gray-700 mb-6">
            Fill out the form below and I'll get back to you as soon as possible.
          </p>
          <form ref={formRef} onSubmit={handleSubmit} className="space-y-4">
            <input
              type="text"
              name="name"
              placeholder="Your Name"
              required
              className="w-full p-3 rounded-lg bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500"
              value={formData.name}
              onChange={handleChange}
            />
            <input
              type="email"
              name="email"
              placeholder="Your Email"
              required
              className="w-full p-3 rounded-lg bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500"
              value={formData.email}
              onChange={handleChange}
            />
            <textarea
              name="message"
              rows="4"
              placeholder="Your Message"
              required
              className="w-full p-3 rounded-lg bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500"
              value={formData.message}
              onChange={handleChange}
            ></textarea>
            <button
              type="submit"
              className="bg-white text-black px-6 py-3 rounded-lg font-semibold hover:bg-gray-200 transition flex items-center gap-2"
            >
              <FaPaperPlane className="text-black" />
              Send Message
            </button>
          </form>
        </motion.div>

        {/* Right: Only Email and WhatsApp Cards */}
        <motion.div
          className="flex flex-col gap-6"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          <ContactCard
            icon={<FaEnvelope className="text-xl text-red-500" />}
            title="Email"
            value="roshithakommana18@gmail.com"
            link="mailto:roshithakommana18@gmail.com"
            buttonText="Send Email"
          />
          <ContactCard
            icon={<FaWhatsapp className="text-xl text-green-600" />}
            title="WhatsApp"
            value="+91 63026 80957"
            link="https://wa.me/916302680957"
            buttonText="Send Message"
          />
        </motion.div>
      </div>

      {/* Popup */}
      {showPopup && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="backdrop-blur-md bg-white/10 text-white p-6 rounded-xl border border-white/20">
            <h4 className="text-xl mb-4">Message Sent Successfully!</h4>
            <button
              onClick={handlePopupClose}
              className="bg-purple-600 px-4 py-2 rounded hover:bg-purple-700"
            >
              OK
            </button>
          </div>
        </div>
      )}

      {/* Signature Footer */}
      <div className="mt-16 flex items-center justify-center gap-6 text-purple-700">
        <a href="https://github.com/Roshitha-Kommana" target="_blank" rel="noopener noreferrer">
          <FaGithub className="text-2xl hover:scale-110 transition" />
        </a>
        <a href="https://linkedin.com/in/roshitha-kommana" target="_blank" rel="noopener noreferrer">
          <FaLinkedin className="text-2xl hover:scale-110 transition" />
        </a>
        <span className="text-2xl italic font-light font-cursive text-black">
          Kommana Roshitha
        </span>
        <a href="https://www.instagram.com/" target="_blank" rel="noopener noreferrer">
          <FaInstagram className="text-2xl hover:scale-110 transition" />
        </a>
        <a href="https://wa.me/916302680957" target="_blank" rel="noopener noreferrer">
          <FaWhatsapp className="text-2xl hover:scale-110 transition" />
        </a>
      </div>
    </section>
  );
};

// Card Component with Button
// Card Component with Button
const ContactCard = ({ icon, title, link, value, buttonText }) => {
  return (
    <motion.div
      className="bg-white/20 backdrop-blur-md p-6 rounded-xl border border-gray-300 shadow-md flex flex-col items-start gap-4 max-w-sm"
      whileHover={{ scale: 1.02, y: -2 }}
      transition={{ type: 'spring', stiffness: 300 }}
    >
      <div className="flex items-center gap-3">
        <div className="text-purple-700">{icon}</div>
        <div>
          <h4 className="text-sm font-semibold text-gray-900">{title}</h4>
          <p className="text-xs text-gray-700">{value}</p>
        </div>
      </div>
      <a
        href={link}
        target="_blank"
        rel="noopener noreferrer"
        className="mt-2 inline-flex items-center gap-2 bg-white text-black px-6 py-3 rounded-lg font-semibold hover:bg-gray-200 transition"
      >
        <FaPaperPlane className="text-black" />
        {buttonText}
      </a>
    </motion.div>
  );
};

export default Contact;
