import React from 'react';
import { motion } from 'framer-motion';

const educationData = [
  {
    id: 3,
    institute: 'XYZ University',
    course: 'B.Tech in Computer Science (Final Year)',
    year: '2021 - Present',
    score: 'CGPA: 8.53',
  },
  {
    id: 2,
    institute: 'Sri Chaitanya Junior College',
    course: 'Intermediate',
    year: 'Completed 2023',
    score: '74.5%',
  },
  {
    id: 1,
    institute: 'Sri Chaitanya Techno School',
    course: '10th Grade',
    year: 'Completed 2021',
    score: '100%',
  },
];

const Education = () => {
  return (
    <section id="education" className="relative max-w-5xl mx-auto py-16 px-6 bg-dark text-white">
      <h2 className="text-3xl font-bold mb-12 text-primary text-center">Education</h2>

      {/* Timeline vertical line */}
      <div className="relative">
        <div className="absolute top-0 left-1/2 -translate-x-1/2 w-[2px] h-full bg-primary"></div>

        {/* Timeline items */}
        {educationData.map(({ id, institute, course, year, score }, index) => {
          const isLeft = index % 2 === 0;

          return (
            <motion.div
              key={id}
              className="mb-12 flex items-center w-full relative"
              initial={{ opacity: 0, x: isLeft ? -80 : 80 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true, amount: 0.7 }}
              transition={{ duration: 0.6, delay: index * 0.3 }}
              style={{ justifyContent: isLeft ? 'flex-end' : 'flex-start' }}
            >
              {/* Card */}
              <div
                className={`bg-gray-800 rounded-lg p-6 shadow-md max-w-[40%] text-center ${
                  isLeft ? 'mr-6' : 'ml-6'
                }`}
                style={{ 
                  // position close to the center line, slight gap
                  marginRight: isLeft ? '20px' : undefined,
                  marginLeft: !isLeft ? '20px' : undefined,
                }}
              >
                <h3 className="text-lg font-semibold mb-2 text-primary">{course}</h3>
                <p className="text-gray-400 mb-1">{institute}</p>
                <p className="italic text-gray-500 mb-3 text-sm">{year}</p>
                <p className="font-medium text-sm">{score}</p>
              </div>

              {/* Dot on center line */}
              <span className="absolute top-1/2 left-1/2 w-4 h-4 rounded-full bg-primary border-2 border-dark -translate-x-1/2 -translate-y-1/2 z-10"></span>
            </motion.div>
          );
        })}
      </div>
    </section>
  );
};

export default Education;
