import React, { useState, useRef } from 'react';
import { motion } from 'framer-motion';
import { FaEnvelope, FaWhatsapp, FaPaperPlane } from 'react-icons/fa';
import emailjs from '@emailjs/browser';

const Contact = () => {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [showPopup, setShowPopup] = useState(false);
  const formRef = useRef();

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    emailjs
      .sendForm(
        'service_ln1b367',
        'template_wgcza8i',
        formRef.current,
        'T-hfPbdDWJ4vL7fyq'
      )
      .then(() => {
        setShowPopup(true);
        setFormData({ name: '', email: '', message: '' });
      })
      .catch((err) => console.error('FAILED...', err));
  };

  const handlePopupClose = () => setShowPopup(false);

  return (
    <section id="contact" className="px-6 py-20 bg-transparent text-gray-900">
      <motion.h2
        className="text-4xl font-bold text-center text-black mb-16"
        initial={{ opacity: 0, y: -20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        Contact Me
      </motion.h2>

      <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-12">
        {/* Contact Form */}
        <motion.div
          className="md:col-span-1 bg-white/20 backdrop-blur-md p-6 rounded-2xl shadow-md border border-gray-200"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <h3 className="text-xl font-semibold text-black-700 mb-3">
            Get in Touch
          </h3>
          <p className="text-gray-700 mb-4 text-sm">
            Drop me a message and Iâ€™ll get back to you soon.
          </p>
          <form
            ref={formRef}
            onSubmit={handleSubmit}
            className="space-y-3"
          >
            <input
              type="text"
              name="name"
              placeholder="Your Name"
              required
              className="w-full p-2 rounded-lg bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500 text-sm"
              value={formData.name}
              onChange={handleChange}
            />
            <input
              type="email"
              name="email"
              placeholder="Your Email"
              required
              className="w-full p-2 rounded-lg bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500 text-sm"
              value={formData.email}
              onChange={handleChange}
            />
            <textarea
              name="message"
              rows="3"
              placeholder="Your Message"
              required
              className="w-full p-2 rounded-lg bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500 text-sm"
              value={formData.message}
              onChange={handleChange}
            ></textarea>
            <button
              type="submit"
              className="bg-purple-600 text-white w-full py-2 rounded-lg font-medium hover:bg-purple-700 transition flex justify-center items-center gap-2"
            >
              <FaPaperPlane className="text-white" />
              Send Message
            </button>
          </form>
        </motion.div>

        {/* Contact Cards */}
        <motion.div
          className="md:col-span-2 flex flex-col gap-6 justify-center items-start"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          <ContactCard
            icon={<FaEnvelope size={24} className="text-white" />}
            title="Email"
            detail="roshithakommana18@gmail.com"
            link="mailto:roshithakommana18@gmail.com"
          />
          <ContactCard
            icon={<FaWhatsapp size={24} className="text-white" />}
            title="WhatsApp"
            detail="+91 6302680957"
            link="https://wa.me/916302680957"
          />
        </motion.div>
      </div>

      {/* Popup */}
      {showPopup && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="backdrop-blur-md bg-white/10 text-white p-6 rounded-xl border border-white/20">
            <h4 className="text-xl mb-4">Message Sent Successfully!</h4>
            <button
              onClick={handlePopupClose}
              className="bg-purple-600 px-4 py-2 rounded hover:bg-purple-700"
            >
              OK
            </button>
          </div>
        </div>
      )}
    </section>
  );
};

// Contact Card Component
const ContactCard = ({ icon, title, detail, link }) => {
  return (
    <motion.a
      href={link}
      target="_blank"
      rel="noopener noreferrer"
      whileHover={{ scale: 1.03 }}
      className="w-full max-w-lg bg-white/30 backdrop-blur-md border border-gray-200 rounded-xl shadow-lg p-6 flex items-center gap-4 hover:bg-white/40 transition"
    >
      <div className="w-12 h-12 bg-purple-600 rounded-full flex items-center justify-center shadow">
        {icon}
      </div>
      <div className="text-left">
        <h4 className="text-lg font-semibold text-gray-900">{title}</h4>
        <p className="text-sm text-gray-700 break-all">{detail}</p>
      </div>
    </motion.a>
  );
};

export default Contact;
