import React from 'react';
import { motion } from 'framer-motion';

const educationData = [
  {
    id: 3,
    institute: 'MVGR College of Engineering',
    course: 'B.Tech in Computer Science',
    year: '2022 - 2026',
    score: 'CGPA: 8.53',
  },
  {
    id: 2,
    institute: 'Sri Chaitanya Junior College',
    course: 'Intermediate - MPC',
    year: '2020 - 2022',
    score: '74.8%',
  },
  {
    id: 1,
    institute: 'Sri Chaitanya Techno School',
    course: 'SSC',
    year: '2019 - 2020',
    score: '99.8%',
  },
];

const Education = () => {
  return (
    <section id="education" className="relative max-w-6xl mx-auto py-16 px-4 bg-[#0b0f19] text-white">
      <h2 className="text-4xl font-bold mb-14 text-center text-purple-400">Education</h2>

      {/* Vertical timeline */}
      <div className="relative before:absolute before:top-0 before:left-1/2 before:-translate-x-1/2 before:h-full before:w-1 before:bg-purple-500">
        {educationData.map(({ id, institute, course, year, score }, index) => {
          const isLeft = index % 2 === 0;

          return (
            <motion.div
              key={id}
              className="mb-20 flex justify-between items-center w-full relative"
              initial={{ opacity: 0, x: isLeft ? -100 : 100 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true, amount: 0.8 }}
              transition={{ duration: 0.6, delay: index * 0.2 }}
            >
              <div
                className={`w-[40%] aspect-square bg-[#111827] rounded-xl p-6 shadow-lg hover:shadow-purple-500/30 transition-all duration-300 text-center ${
                  isLeft ? 'ml-auto' : 'mr-auto'
                }`}
              >
                <h3 className="text-lg md:text-xl font-semibold text-purple-400 mb-2">{course}</h3>
                <p className="text-gray-300 mb-1">{institute}</p>
                <p className="text-gray-400 italic mb-2">{year}</p>
                <p className="text-white font-medium">{score}</p>
              </div>

              {/* Center dot */}
              <span className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-4 h-4 rounded-full bg-purple-500 border-[3px] border-[#0b0f19] z-10 shadow-md"></span>
            </motion.div>
          );
        })}
      </div>
    </section>
  );
};

export default Education;
