import React from 'react';

const skills = [
  {
    title: 'Languages',
    items: ['Java', 'Python', 'C++', 'C', 'HTML', 'CSS', 'JavaScript', 'Tailwind CSS', 'React.js'],
  },
  {
    title: 'Backend & Databases',
    items: ['Firebase', 'MongoDB', 'MySQL'],
  },
  {
    title: 'Tools & Libraries',
    items: ['OpenCV', 'Tesseract OCR', 'Git', 'VS Code'],
  },
  {
    title: 'Concepts',
    items: ['OOP', 'DBMS', 'Data Structures', 'Machine Learning', 'Big Data', 'UI/UX Principles'],
  },
];

const Skills = () => {
  return (
    <section id="skills" className="bg-[#0b0f19] text-white py-20 px-6 max-w-6xl mx-auto">
      <h2 className="text-4xl font-bold text-center text-purple-400 mb-16">Skills</h2>
      
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 gap-8">
        {skills.map((skill, index) => (
          <div
            key={index}
            className="bg-[#111827] p-6 rounded-xl shadow-md hover:shadow-purple-400/20 transition-all"
          >
            <h3 className="text-xl font-semibold text-purple-400 mb-4">{skill.title}</h3>
            <ul className="list-disc list-inside space-y-1 text-gray-300">
              {skill.items.map((item, idx) => (
                <li key={idx}>{item}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Skills;
