import React from 'react';
import { ReactTyped } from 'react-typed';
import { FaGithub, FaLinkedin, FaInstagram } from 'react-icons/fa';
import { motion } from 'framer-motion';

const Hero = () => {
  return (
    <section className="text-white min-h-[90vh] flex items-center justify-center px-8">
      <div className="max-w-6xl grid md:grid-cols-2 gap-12 items-center">
        {/* Left side: Typed text + description + buttons + social */}
        <motion.div
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
          className="flex flex-col gap-6"
        >
          <h1 className="text-5xl font-bold leading-tight">
            I am a{' '}
            <span className="text-primary">
              <ReactTyped
                strings={['Web Developer.', 'UI/UX Designer.', 'Open Source Enthusiast.']}
                typeSpeed={50}
                backSpeed={30}
                loop
              />
            </span>
          </h1>

          <p className="text-lg text-gray-300 max-w-xl">
            Enhancing digital experiences that are smooth, scalable, and made to impress.
          </p>

          <div className="flex gap-4">
            <a
              href="/resume.pdf"
              target="_blank"
              rel="noopener noreferrer"
              className="bg-primary px-6 py-2 rounded shadow hover:bg-primary-dark transition"
            >
              View Resume
            </a>
            <a
              href="/resume.pdf"
              download
              className="border border-primary px-6 py-2 rounded hover:bg-primary hover:text-white transition"
            >
              Download Resume
            </a>
          </div>

          <div className="flex gap-6 text-3xl text-gray-400 mt-4">
            <a href="https://github.com/Roshitha-Kommana" target="_blank" rel="noopener noreferrer" className="hover:text-primary">
              <FaGithub />
            </a>
            <a href="https://linkedin.com/in/roshitha-kommana" target="_blank" rel="noopener noreferrer" className="hover:text-primary">
              <FaLinkedin />
            </a>
            <a href="https://instagram.com" target="_blank" rel="noopener noreferrer" className="hover:text-primary">
              <FaInstagram />
            </a>
          </div>
        </motion.div>

        {/* Right side: Profile image */}
        <motion.div
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
          className="flex justify-center"
        >
          <img
            src="/profile.jpg"
            alt="Profile"
            className="rounded-lg shadow-xl w-64 h-64 object-cover"
          />
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
