import React from 'react';
import { motion } from 'framer-motion';

const Projects = () => {
  return (
    <motion.section
      id="projects"
      className="text-white px-8 py-16"
      initial={{ opacity: 0, y: 50 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
      viewport={{ once: true }}
    >
      <h2 className="text-3xl font-bold text-primary mb-4">Projects</h2>
      <ul className="space-y-4 text-gray-300">
        <li><strong>DonateEase</strong>: A web app for connecting donors and NGOs.</li>
        <li><strong>Smart OCR</strong>: Automated text extraction from scanned PDFs using Python and Tesseract.</li>
      </ul>
    </motion.section>
  );
};

export default Projects;
