import React from 'react';
import { motion } from 'framer-motion';

const educationData = [
  {
    id: 3,
    institute: 'XYZ University',
    course: 'B.Tech in Computer Science (Final Year)',
    year: '2021 - Present',
    score: 'CGPA: 8.53',
  },
  {
    id: 2,
    institute: 'Sri Chaitanya Junior College',
    course: 'Intermediate',
    year: 'Completed 2023',
    score: '74.5%',
  },
  {
    id: 1,
    institute: 'Sri Chaitanya Techno School',
    course: '10th Grade',
    year: 'Completed 2021',
    score: '100%',
  },
];

const Education = () => {
  return (
    <section id="education" className="relative max-w-6xl mx-auto py-16 px-6 bg-dark text-white">
      <h2 className="text-3xl font-bold mb-12 text-primary text-center">Education</h2>

      {/* Timeline container */}
      <div className="relative before:absolute before:top-0 before:left-1/2 before:-translate-x-1/2 before:h-full before:w-[2px] before:bg-primary">
        {educationData.map(({ id, institute, course, year, score }, index) => {
          const isLeft = index % 2 === 0;

          return (
            <motion.div
              key={id}
              className="mb-12 flex w-full items-center justify-between relative"
              initial={{ opacity: 0, x: isLeft ? -100 : 100 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true, amount: 0.8 }}
              transition={{ duration: 0.6, delay: index * 0.2 }}
            >
              <div
                className={`w-[40%] bg-gray-800 rounded-lg p-4 shadow-lg text-center text-sm md:text-base ${
                  isLeft ? 'ml-auto' : 'mr-auto'
                }`}
              >
                <h3 className="text-lg font-semibold text-primary mb-1">{course}</h3>
                <p className="text-gray-400 mb-1">{institute}</p>
                <p className="italic text-gray-500 mb-2">{year}</p>
                <p className="font-medium">{score}</p>
              </div>

              {/* Dot on the center line */}
              <span className="absolute top-1/2 left-1/2 w-3 h-3 rounded-full bg-primary border-2 border-dark -translate-x-1/2 -translate-y-1/2 z-10"></span>
            </motion.div>
          );
        })}
      </div>
    </section>
  );
};

export default Education;
