import React from 'react';
import { motion } from 'framer-motion';

const educationData = [
  {
    id: 3,
    institute: 'MVGR College of Engineering',
    course: 'B.Tech in CSE',
    year: '2022 - 2026',
    score: 'CGPA: 8.53',
  },
  {
    id: 2,
    institute: 'Sri Chaitanya Junior College',
    course: 'Intermediate - MPC',
    year: '2020 - 2022',
    score: '74.8%',
  },
  {
    id: 1,
    institute: 'Sri Chaitanya Techno School',
    course: 'SSC',
    year: '2019 - 2020',
    score: '99.8%',
  },
];

const Education = () => {
  return (
    <section id="education" className="relative max-w-5xl mx-auto py-20 px-4 bg-[#0b0f19] text-white">
      <h2 className="text-4xl font-bold mb-20 text-center text-purple-400">Education</h2>

      {/* Timeline vertical line */}
      <div className="relative before:absolute before:top-0 before:left-1/2 before:-translate-x-1/2 before:h-full before:w-[2px] before:bg-purple-500">
        {educationData.map(({ id, institute, course, year, score }, index) => {
          const isLeft = index % 2 === 0;

          return (
            <motion.div
              key={id}
              className={`mb-16 w-full flex items-center justify-between relative`}
              initial={{ opacity: 0, x: isLeft ? -100 : 100 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true, amount: 0.8 }}
              transition={{ duration: 0.6, delay: index * 0.2 }}
            >
              {/* Left card */}
              {isLeft && (
                <div className="w-[45%] flex justify-end pr-4">
                  <div className="bg-[#111827] w-full max-w-xs h-40 rounded-lg p-4 shadow-md text-center flex flex-col justify-center items-center hover:shadow-purple-400/30">
                    <h3 className="text-base font-semibold text-purple-400 mb-1">{course}</h3>
                    <p className="text-gray-300 mb-1">{institute}</p>
                    <p className="text-gray-400 italic mb-1">{year}</p>
                    <p className="text-white font-medium">{score}</p>
                  </div>
                </div>
              )}

              {/* Timeline Dot */}
              <div className="absolute left-1/2 top-1/2 transform -translate-x-1/2 -translate-y-1/2 z-10">
                <div className="w-4 h-4 bg-purple-500 border-4 border-[#0b0f19] rounded-full shadow-lg" />
              </div>

              {/* Right card */}
              {!isLeft && (
                <div className="w-[45%] flex justify-start pl-4">
                  <div className="bg-[#111827] w-full max-w-xs h-40 rounded-lg p-4 shadow-md text-center flex flex-col justify-center items-center hover:shadow-purple-400/30">
                    <h3 className="text-base font-semibold text-purple-400 mb-1">{course}</h3>
                    <p className="text-gray-300 mb-1">{institute}</p>
                    <p className="text-gray-400 italic mb-1">{year}</p>
                    <p className="text-white font-medium">{score}</p>
                  </div>
                </div>
              )}
            </motion.div>
          );
        })}
      </div>
    </section>
  );
};

export default Education;
