import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FiBox, FiAward } from 'react-icons/fi'; // Icons for tabs
import { AiOutlineLink } from 'react-icons/ai'; // For links

// Dummy images for demo, replace with your real project images
const projectImages = {
  DonateEase: 'https://source.unsplash.com/400x250/?technology,app',
  SmartOCR: 'https://source.unsplash.com/400x250/?technology,document',
};

const projects = [
  {
    title: 'DonateEase Platform',
    description: 'User-friendly platform connecting donors with NGOs.',
    fullDescription:
      'DonateEase is a comprehensive platform designed to connect donors with NGOs effectively. It offers a seamless donation process including multiple donation types, payment options, and real-time tracking.',
    link: 'https://example.com/donatease-live',
    image: projectImages.DonateEase,
  },
  {
    title: 'Smart OCR',
    description: 'Automated text extraction from scanned documents.',
    fullDescription:
      'Smart OCR uses advanced image processing and Tesseract OCR to extract text from scanned PDFs and images with high accuracy and efficiency, supporting multiple preprocessing techniques.',
    link: 'https://example.com/smartocr-live',
    image: projectImages.SmartOCR,
  },
];

const certificates = [
  {
    title: 'Artificial Intelligence Fundamentals',
    issuer: 'IBM SkillsBuild',
    date: 'Feb 2025',
    link: '#',
  },
  {
    title: 'AWS Academy Cloud Foundations',
    issuer: 'Amazon Web Services',
    date: 'Dec 2024',
    link: '#',
  },
];

const tabButtonStyle = (active) =>
  `flex items-center gap-2 px-6 py-2 rounded-lg font-semibold cursor-pointer
   transition-all duration-300
   ${
     active
       ? 'bg-white/20 backdrop-blur-md border border-purple-500 text-purple-300 shadow-[0_0_10px_rgba(124,58,237,0.7)]'
       : 'text-gray-400 hover:text-purple-300 hover:bg-white/10 border border-transparent'
   }`;

const ProjectsCertificates = () => {
  const [activeTab, setActiveTab] = useState('projects');
  const [modalProject, setModalProject] = useState(null);

  return (
    <section className="max-w-7xl mx-auto py-16 px-6 bg-[#0b0f19] text-white select-none">
      <h2 className="text-4xl font-extrabold mb-10 text-center text-purple-400 tracking-wide">
        Projects & Certificates
      </h2>

      {/* Tabs */}
      <div className="flex justify-center mb-12 gap-4">
        <button
          className={tabButtonStyle(activeTab === 'projects')}
          onClick={() => setActiveTab('projects')}
          aria-label="View Projects"
        >
          <FiBox size={20} />
          Projects
        </button>
        <button
          className={tabButtonStyle(activeTab === 'certificates')}
          onClick={() => setActiveTab('certificates')}
          aria-label="View Certificates"
        >
          <FiAward size={20} />
          Certificates
        </button>
      </div>

      {/* Content */}
      <AnimatePresence mode="wait">
        {activeTab === 'projects' ? (
          <motion.div
            key="projects"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            transition={{ duration: 0.3 }}
            className="grid grid-cols-1 md:grid-cols-2 gap-10"
          >
            {projects.map(({ title, description, fullDescription, link, image }) => (
              <div
                key={title}
                className="bg-[#1e2235] rounded-xl shadow-lg hover:shadow-purple-600 transition-shadow flex flex-col"
              >
                <img
                  src={image}
                  alt={title}
                  className="rounded-t-xl object-cover w-full h-48"
                />
                <div className="p-6 flex flex-col flex-grow">
                  <h3 className="text-2xl font-semibold text-purple-300 mb-2">{title}</h3>
                  <p className="text-gray-300 flex-grow">{description}</p>
                </div>

                <div className="px-6 py-4 flex justify-between border-t border-purple-700">
                  <a
                    href={link}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex items-center gap-1 text-purple-400 hover:text-purple-600 font-semibold transition"
                  >
                    <AiOutlineLink />
                    Live Demo
                  </a>
                  <button
                    onClick={() => setModalProject({ title, fullDescription, image })}
                    className="bg-purple-600 hover:bg-purple-700 px-4 py-1 rounded-md font-semibold transition"
                  >
                    Full Details &rarr;
                  </button>
                </div>
              </div>
            ))}
          </motion.div>
        ) : (
          <motion.div
            key="certificates"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            transition={{ duration: 0.3 }}
            className="grid grid-cols-1 md:grid-cols-2 gap-8"
          >
            {certificates.map(({ title, issuer, date, link }) => (
              <a
                key={title}
                href={link}
                target="_blank"
                rel="noopener noreferrer"
                className="bg-[#1e2235] p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow flex flex-col cursor-pointer"
              >
                <h3 className="text-xl font-semibold text-purple-300 mb-1">{title}</h3>
                <p className="text-gray-400 italic mb-1">{issuer}</p>
                <p className="text-gray-400 text-sm">{date}</p>
              </a>
            ))}
          </motion.div>
        )}
      </AnimatePresence>

      {/* Modal for Full Project Details */}
      {modalProject && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-black bg-opacity-80 flex items-center justify-center z-50 p-4"
          onClick={() => setModalProject(null)}
        >
          <motion.div
            initial={{ scale: 0.8 }}
            animate={{ scale: 1 }}
            exit={{ scale: 0.8 }}
            transition={{ duration: 0.25 }}
            className="bg-[#1e2235] rounded-xl max-w-3xl w-full p-8 relative overflow-y-auto max-h-[90vh]"
            onClick={(e) => e.stopPropagation()}
          >
            <button
              onClick={() => setModalProject(null)}
              className="absolute top-4 right-4 text-purple-400 hover:text-purple-600 text-2xl font-bold"
              aria-label="Close Modal"
            >
              &times;
            </button>
            <img
              src={modalProject.image}
              alt={modalProject.title}
              className="rounded-lg mb-6 object-cover w-full max-h-60"
            />
            <h3 className="text-3xl font-bold text-purple-300 mb-4">{modalProject.title}</h3>
            <p className="text-gray-300 whitespace-pre-line">{modalProject.fullDescription}</p>
          </motion.div>
        </motion.div>
      )}
    </section>
  );
};

export default ProjectsCertificates;
