import React from 'react';
import { motion } from 'framer-motion';
import { 
  SiJava, SiPython, SiCplusplus, SiHtml5, SiCss3, SiJavascript, SiTailwindcss, SiReact,
  SiFirebase, SiMongodb, SiMysql,
  SiOpencv, SiGit, SiVisualstudiocode,
  SiMachinelearning, SiBigbluebutton // just placeholders for concepts icons, you can customize
} from 'react-icons/si';
import { MdStorage, MdOutlineDesignServices, MdOutlineGroups } from 'react-icons/md';

const skillsData = [
  {
    category: 'Languages',
    icon: <SiJavascript className="text-purple-400" size={24} />,
    skills: [
      { icon: <SiJava size={18} />, label: 'Java' },
      { icon: <SiPython size={18} />, label: 'Python' },
      { icon: <SiCplusplus size={18} />, label: 'C++' },
      { icon: <SiJavascript size={18} />, label: 'JavaScript' },
      { icon: <SiHtml5 size={18} />, label: 'HTML' },
      { icon: <SiCss3 size={18} />, label: 'CSS' },
      { icon: <SiTailwindcss size={18} />, label: 'Tailwind CSS' },
      { icon: <SiReact size={18} />, label: 'React.js' },
    ],
  },
  {
    category: 'Backend & Databases',
    icon: <MdStorage className="text-purple-400" size={24} />,
    skills: [
      { icon: <SiFirebase size={18} />, label: 'Firebase' },
      { icon: <SiMongodb size={18} />, label: 'MongoDB' },
      { icon: <SiMysql size={18} />, label: 'MySQL' },
    ],
  },
  {
    category: 'Tools & Libraries',
    icon: <SiGit className="text-purple-400" size={24} />,
    skills: [
      { icon: <SiOpencv size={18} />, label: 'OpenCV' },
      { icon: <SiPython size={18} />, label: 'Tesseract OCR' },
      { icon: <SiGit size={18} />, label: 'Git' },
      { icon: <SiVisualstudiocode size={18} />, label: 'VS Code' },
    ],
  },
  {
    category: 'Concepts',
    icon: <MdOutlineDesignServices className="text-purple-400" size={24} />,
    skills: [
      { icon: <MdOutlineDesignServices size={18} />, label: 'OOP' },
      { icon: <MdStorage size={18} />, label: 'DBMS' },
      { icon: <MdOutlineGroups size={18} />, label: 'Data Structures' },
      { icon: <SiMachinelearning size={18} />, label: 'Machine Learning' },
      { icon: <SiBigbluebutton size={18} />, label: 'Big Data' },
      { icon: <MdOutlineDesignServices size={18} />, label: 'UI/UX Principles' },
    ],
  },
  {
    category: 'Key Strengths',
    icon: <MdOutlineGroups className="text-purple-400" size={24} />,
    skills: [
      { icon: <MdOutlineGroups size={18} />, label: 'Problem Solving' },
      { icon: <MdOutlineGroups size={18} />, label: 'Team Collaboration' },
      { icon: <MdOutlineGroups size={18} />, label: 'Active Listener' },
      { icon: <MdOutlineGroups size={18} />, label: 'Time Management' },
    ],
  },
];

const containerVariant = {
  hidden: { opacity: 0, y: 20 },
  visible: {
    opacity: 1,
    y: 0,
    transition: {
      staggerChildren: 0.15,
    },
  },
};

const cardVariant = {
  hidden: { opacity: 0, scale: 0.9 },
  visible: { opacity: 1, scale: 1, transition: { duration: 0.4 } },
  hover: { scale: 1.05, boxShadow: '0 0 15px rgba(139, 92, 246, 0.6)' },
};

const Skills = () => {
  return (
    <section id="skills" className="max-w-6xl mx-auto py-20 px-6 bg-[#0b0f19] text-white">
      <h2 className="text-4xl font-bold mb-16 text-center text-purple-400">Skills & Strengths</h2>

      <motion.div
        className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6"
        variants={containerVariant}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: false, amount: 0.4 }} // animates every time section enters viewport
      >
        {skillsData.map(({ category, icon, skills }) => (
          <motion.div
            key={category}
            variants={cardVariant}
            whileHover="hover"
            className="bg-[#1e2235] rounded-lg p-6 shadow-md cursor-pointer border border-transparent hover:border-purple-600 transition-all flex flex-col"
          >
            <div className="flex items-center mb-4 space-x-3">
              <div className="text-purple-400">{icon}</div>
              <h3 className="text-xl font-semibold">{category}</h3>
            </div>

            <div className="flex flex-wrap gap-3 mt-auto">
              {skills.map(({ icon, label }) => (
                <div
                  key={label}
                  className="flex items-center space-x-2 bg-[#111827] rounded-md px-3 py-1 text-sm font-medium text-gray-300 hover:text-purple-400 hover:bg-[#2c2f4a] transition"
                >
                  <div>{icon}</div>
                  <span>{label}</span>
                </div>
              ))}
            </div>
          </motion.div>
        ))}
      </motion.div>
    </section>
  );
};

export default Skills;
