import React from 'react';

const Projects = () => {
  const projects = [
    {
      title: 'DonateEase',
      desc: 'A donation platform connecting donors with NGOs. Supports money, books, and clothes donations.',
    },
    {
      title: 'Smart OCR',
      desc: 'An OCR pipeline that extracts structured text from scanned PDFs using Tesseract, OpenCV, and Python.',
    },
    {
      title: 'Resume Chatbot',
      desc: 'A Botpress-based chatbot that responds to resume-based queries using stored user data.',
    },
  ];

  return (
    <section id="projects" className="text-white py-16 px-8">
      <div className="max-w-5xl mx-auto">
        <h2 className="text-4xl font-bold text-center text-primary mb-8">Projects</h2>
        <div className="grid md:grid-cols-2 gap-6">
          {projects.map((project, index) => (
            <div key={index} className="bg-gray-900 p-6 rounded-lg shadow">
              <h3 className="text-2xl font-semibold mb-2">{project.title}</h3>
              <p className="text-gray-300">{project.desc}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
