// Education.jsx
import React from 'react';
import { motion } from 'framer-motion';

const Education = () => {
  return (
    <section id="education" className="text-white px-6 py-16">
      <motion.h2
        className="text-3xl font-bold mb-10 text-primary text-center"
        initial={{ opacity: 0, y: -20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        viewport={{ once: true }}
      >
        Education
      </motion.h2>

      <div className="max-w-4xl mx-auto space-y-6">
        <motion.div
          className="bg-white/10 backdrop-blur-sm p-6 rounded-lg shadow-lg"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          viewport={{ once: true }}
        >
          <h3 className="text-xl font-semibold text-primary">B.Tech in Computer Science</h3>
          <p className="text-gray-300">XYZ University, 2021 - 2025</p>
          <p className="text-gray-400 mt-2">
            Relevant Coursework: Data Structures, Algorithms, Operating Systems, DBMS, Web Development.
          </p>
        </motion.div>

        <motion.div
          className="bg-white/10 backdrop-blur-sm p-6 rounded-lg shadow-lg"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.4 }}
          viewport={{ once: true }}
        >
          <h3 className="text-xl font-semibold text-primary">Intermediate Education</h3>
          <p className="text-gray-300">ABC Junior College, 2019 - 2021</p>
          <p className="text-gray-400 mt-2">
            Studied MPC (Maths, Physics, Chemistry) with distinction.
          </p>
        </motion.div>
      </div>
    </section>
  );
};

export default Education;
