import React, { useState, useRef } from 'react';
import { motion } from 'framer-motion';
import {
  FaGithub, FaLinkedin, FaEnvelope, FaWhatsapp, FaInstagram, FaPaperPlane
} from 'react-icons/fa';
import emailjs from '@emailjs/browser';

const Contact = () => {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [showPopup, setShowPopup] = useState(false);
  const formRef = useRef();

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    emailjs.sendForm(
      'service_ln1b367',
      'template_wgcza8i',
      formRef.current,
      'T-hfPbdDWJ4vL7fyq'
    )
    .then(() => {
      setShowPopup(true);
      setFormData({ name: '', email: '', message: '' });
    })
    .catch((err) => console.error('FAILED...', err));
  };

  const handlePopupClose = () => setShowPopup(false);

  return (
    <section id="contact" className="px-6 py-20 bg-transparent text-gray-900">
      <motion.h2
        className="text-4xl font-bold text-center text-black mb-16"
        initial={{ opacity: 0, y: -20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        Contact Me
      </motion.h2>

      <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-12 items-start">
        {/* Left: Mesmerizing Form */}
        <motion.div
          className="relative p-1 bg-gradient-to-br from-purple-400 via-pink-300 to-yellow-200 rounded-3xl shadow-xl"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <div className="bg-white/30 backdrop-blur-xl rounded-3xl p-8 h-full">
            <h3 className="text-2xl font-bold text-black mb-4">Get in Touch with Me</h3>
            <p className="text-sm text-gray-700 mb-6">
              Fill out the form below and I’ll respond shortly.
            </p>
            <form ref={formRef} onSubmit={handleSubmit} className="space-y-4">
              <input
                type="text"
                name="name"
                placeholder="Your Name"
                required
                className="w-full p-3 rounded-lg bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500"
                value={formData.name}
                onChange={handleChange}
              />
              <input
                type="email"
                name="email"
                placeholder="Your Email"
                required
                className="w-full p-3 rounded-lg bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500"
                value={formData.email}
                onChange={handleChange}
              />
              <textarea
                name="message"
                rows="4"
                placeholder="Your Message"
                required
                className="w-full p-3 rounded-lg bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500"
                value={formData.message}
                onChange={handleChange}
              ></textarea>
              <button
                type="submit"
                className="bg-black text-white px-6 py-3 rounded-xl font-semibold hover:bg-gray-800 transition flex items-center gap-2"
              >
                <FaPaperPlane />
                Send Message
              </button>
            </form>
          </div>
        </motion.div>

        {/* Right: Mail and WhatsApp Cards */}
        <motion.div
          className="flex flex-col gap-6"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <ContactCard
            icon={<FaEnvelope className="text-2xl text-red-500" />}
            title="Email"
            actionLabel="Send Email"
            link="mailto:roshithakommana18@gmail.com"
            value="roshithakommana18@gmail.com"
          />
          <ContactCard
            icon={<FaWhatsapp className="text-2xl text-green-600" />}
            title="WhatsApp"
            actionLabel="Send WhatsApp"
            link="https://wa.me/916302680957"
            value="+91 63026 80957"
          />
        </motion.div>
      </div>

      {/* Success Popup */}
      {showPopup && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="backdrop-blur-md bg-white/10 text-white p-6 rounded-xl border border-white/20">
            <h4 className="text-xl mb-4">Message Sent Successfully!</h4>
            <button
              onClick={handlePopupClose}
              className="bg-purple-600 px-4 py-2 rounded hover:bg-purple-700"
            >
              OK
            </button>
          </div>
        </div>
      )}

      {/* Bottom Transparent Footer Section */}
      <div className="mt-24 py-6 px-4 bg-white/10 backdrop-blur-lg border border-white/20 rounded-2xl flex flex-col md:flex-row justify-between items-center text-white gap-6">
        {/* Left icons */}
        <div className="flex gap-4 items-center">
          <a href="https://github.com/Roshitha-Kommana" target="_blank" rel="noopener noreferrer">
            <FaGithub className="text-2xl hover:scale-110 transition" />
          </a>
          <a href="https://linkedin.com/in/roshitha-kommana" target="_blank" rel="noopener noreferrer">
            <FaLinkedin className="text-2xl hover:scale-110 transition" />
          </a>
        </div>

        {/* Center name */}
        <span className="text-xl text-black font-light italic">Kommana Roshitha</span>

        {/* Right icons */}
        <div className="flex gap-4 items-center">
          <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
            <FaInstagram className="text-2xl hover:scale-110 transition" />
          </a>
          <a href="https://wa.me/916302680957" target="_blank" rel="noopener noreferrer">
            <FaWhatsapp className="text-2xl hover:scale-110 transition" />
          </a>
        </div>
      </div>
    </section>
  );
};

// ContactCard Component
const ContactCard = ({ icon, title, value, link, actionLabel }) => {
  return (
    <motion.div
      className="bg-white/20 backdrop-blur-md p-6 rounded-2xl border border-gray-200 flex flex-col gap-3 shadow-md"
      whileHover={{ scale: 1.02 }}
    >
      <div className="flex items-center gap-3">
        <div>{icon}</div>
        <div>
          <h4 className="text-lg font-semibold text-gray-900">{title}</h4>
          <p className="text-sm text-gray-700">{value}</p>
        </div>
      </div>
      <a
        href={link}
        target="_blank"
        rel="noopener noreferrer"
        className="mt-2 inline-block w-max bg-black text-white px-4 py-2 rounded-md text-sm hover:bg-gray-800 transition"
      >
        {actionLabel}
      </a>
    </motion.div>
  );
};

export default Contact;
