];


const CertificatesCarousel = ({ certificates }) => {
  const [selectedCert, setSelectedCert] = useState(null);
  const [startIndex, setStartIndex] = useState(0);
  const itemsPerPage = 3;

  const visibleCerts = certificates.slice(startIndex, startIndex + itemsPerPage);

  const nextSlide = () => {
    if (startIndex + itemsPerPage < certificates.length) {
      setStartIndex(startIndex + 1);
    }
  };

  const prevSlide = () => {
    if (startIndex > 0) {
      setStartIndex(startIndex - 1);
    }
  };

  return (
    <div className="w-full flex flex-col items-center">
      {/* Buttons */}
      <div className="flex justify-center items-center space-x-4 mb-4">
        <button
          onClick={prevSlide}
          disabled={startIndex === 0}
          className={`rounded-full p-2 text-sm font-bold transition ${
            startIndex === 0
              ? 'opacity-50 cursor-not-allowed bg-white text-gray-400'
              : 'bg-white text-purple-700 shadow-md hover:bg-purple-200'
          }`}
        >
          «
        </button>

        <button
          onClick={nextSlide}
          disabled={startIndex + itemsPerPage >= certificates.length}
          className={`rounded-full p-2 text-sm font-bold transition ${
            startIndex + itemsPerPage >= certificates.length
              ? 'opacity-50 cursor-not-allowed bg-white text-gray-400'
              : 'bg-white text-purple-700 shadow-md hover:bg-purple-200'
          }`}
        >
          »
        </button>
      </div>

      {/* Carousel */}
      <div className="flex justify-center items-center gap-6 flex-wrap transition-all duration-300 ease-in-out">
        {visibleCerts.map((cert, idx) => (
          <motion.div
            key={cert.id}
            className="relative w-64 h-52 flex-shrink-0 bg-white/30 border border-white/20 backdrop-blur-md rounded-lg shadow-md p-2 group transition hover:shadow-xl"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3, delay: idx * 0.1 }}
          >
            <div className="w-full h-full flex items-center justify-center">
              <img
                src={cert.imageUrl}
                alt={`Certificate ${cert.id}`}
                className="w-full h-full object-contain rounded-md"
                loading="eager"
                decoding="async"
                fetchpriority="high"
              />
            </div>

            {/* Hover Overlay */}
            <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-50 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
              <button
                onClick={() => setSelectedCert(cert)}
                className="text-white text-lg font-semibold hover:underline transition"
              >
                View Certificate
              </button>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Modal */}
      {selectedCert && (
        <div className="fixed inset-0 bg-black bg-opacity-90 flex items-center justify-center z-50">
          <button
            onClick={() => setSelectedCert(null)}
            className="absolute top-6 right-6 text-white text-3xl hover:text-purple-400"
          >
            &times;
          </button>
          <img
            src={selectedCert.imageUrl}
            alt="Certificate Full View"
            className="max-w-[90vw] max-h-[90vh] rounded-xl"
          />
        </div>
      )}
    </div>
  );
};

const BadgesCarousel = ({ badges }) => {
  const [startIndex, setStartIndex] = useState(0);
  const itemsPerPage = 3;

  const visibleBadges = badges.slice(startIndex, startIndex + itemsPerPage);

  const nextSlide = () => {
    if (startIndex + itemsPerPage < badges.length) setStartIndex(startIndex + 1);
  };

  const prevSlide = () => {
    if (startIndex > 0) setStartIndex(startIndex - 1);
  };

  const [hasAnimated, setHasAnimated] = useState(false);
  useEffect(() => {
    const timeout = setTimeout(() => setHasAnimated(true), 100);
    return () => clearTimeout(timeout);
  }, []);

  return (
    <div className="relative w-full flex flex-col items-center">
      <div className="flex justify-center items-center space-x-4">
        <button
          onClick={prevSlide}
          disabled={startIndex === 0}
          className={`bg-purple-300 text-white rounded-full p-1 text-sm ${
            startIndex === 0 ? 'opacity-50 cursor-not-allowed' : 'hover:bg-purple-500'
          }`}
        >
          «
        </button>

        <div
          className={`flex space-x-6 transform transition-transform duration-700 ease-out ${
            hasAnimated ? 'translate-x-0 opacity-100' : 'translate-x-20 opacity-0'
          }`}
        >
          {visibleBadges.map((badge) => (
            <a
              key={badge.id}
              href={badge.link}
              target="_blank"
              rel="noopener noreferrer"
              className="w-40 h-40 sm:w-44 sm:h-44 md:w-48 md:h-48 flex-shrink-0 bg-white/40 border border-white/30 backdrop-blur-md rounded-xl overflow-hidden hover:shadow-xl transition-shadow flex items-center justify-center"
            >
              <img
                src={badge.imageUrl}
                alt={`Badge ${badge.id}`}
                className="w-[85%] h-[85%] object-contain"
              />
            </a>
          ))}
        </div>

        <button
          onClick={nextSlide}
          disabled={startIndex + itemsPerPage >= badges.length}
          className={`bg-purple-300 text-white rounded-full p-1 text-sm ${
            startIndex + itemsPerPage >= badges.length ? 'opacity-50 cursor-not-allowed' : 'hover:bg-purple-500'
          }`}
        >
          »
        </button>
      </div>
    </div>
  );
};


const ProjectsCertificates = () => {
