    <section className="px-6 py-20 text-gray-900 bg-transparent">
      <motion.h2
        className="text-4xl font-bold mb-12 text-center text-[#4B0082]"
        initial={{ opacity: 0, y: -30 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
