                  {proj.liveDemo ? (
                    <a href={proj.liveDemo} target="_blank" rel="noopener noreferrer" className="hover:underline">
                      Live Demo
                    </a>
                  ) : (
                    <span className="italic text-gray-500">No Demo</span>
                  )}
                  <Link to={`/projects/${proj.id}`} className="hover:underline font-semibold">
                    Full Details →
                  </Link>
                </div>
              </motion.div>
            ))}
          </motion.div>

          {!showAll && projects.length > 3 && (
            <div className="flex justify-center mt-10">
              <motion.button
                onClick={() => setShowAll(true)}
                className="text-black hover:text-purple-800 text-lg font-medium"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                See More ↓
              </motion.button>
            </div>
          )}
        </>
      ) : activeTab === 'certificates' ? (
        <motion.div
          initial={{ opacity: 0, x: 50 }}
          whileInView={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.6 }}
        >
          <CertificatesCarousel certificates={certificates} />
        </motion.div>
      ) : (
        <motion.div
          initial={{ opacity: 0, x: 50 }}
          whileInView={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.6 }}
        >
          <BadgesCarousel badges={badges} />
        </motion.div>
      )}
    </section>
  );
};



