import React from 'react';
import { motion } from 'framer-motion';
import { FaUserGraduate, FaFolderOpen } from 'react-icons/fa';
import { Link } from 'react-scroll';

const About = () => {
  return (
    <section id="about" className="px-6 py-16 text-white bg-dark">
      {/* Heading */}
      <motion.h2
        initial={{ opacity: 0, y: -20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        viewport={{ once: true }}
        className="text-4xl font-bold text-center mb-12 text-primary"
      >
        About Me
      </motion.h2>

      {/* Cards Opposite to Each Other */}
      <div className="relative max-w-6xl mx-auto">
        <div className="absolute left-1/2 top-0 w-1 bg-primary h-full -translate-x-1/2"></div>

        <div className="flex flex-col gap-12">
          {/* Left Side Card */}
          <motion.div
            className="relative w-1/2 pr-8 self-end"
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <div className="bg-gray-900 p-6 rounded-lg shadow-md flex items-center gap-4">
              <FaUserGraduate className="text-3xl text-primary" />
              <div>
                <h3 className="text-lg font-semibold">Final Year Student</h3>
                <p className="text-gray-400 text-sm">Currently pursuing B.Tech in Computer Science.</p>
              </div>
            </div>
            <span className="absolute top-1/2 right-[-10px] transform -translate-y-1/2 w-5 h-5 bg-primary border-4 border-dark rounded-full"></span>
          </motion.div>

          {/* Right Side Card */}
          <motion.div
            className="relative w-1/2 pl-8 self-start"
            initial={{ opacity: 0, x: 50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            <div className="bg-gray-900 p-6 rounded-lg shadow-md flex items-center gap-4 justify-between">
              <FaFolderOpen className="text-3xl text-primary" />
              <div className="flex-1">
                <h3 className="text-lg font-semibold">Projects Completed</h3>
                <p className="text-gray-400 text-sm">10+ successfully built and deployed projects.</p>
              </div>
              <Link
                to="projects"
                smooth={true}
                duration={500}
                className="bg-primary text-white px-3 py-1.5 text-sm rounded hover:bg-opacity-80 cursor-pointer"
              >
                View
              </Link>
            </div>
            <span className="absolute top-1/2 left-[-10px] transform -translate-y-1/2 w-5 h-5 bg-primary border-4 border-dark rounded-full"></span>
          </motion.div>
        </div>
      </div>

      {/* Description */}
      <motion.div
        className="max-w-4xl mx-auto mt-16 text-lg text-gray-300 text-center"
        initial={{ opacity: 0, y: 20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6, delay: 0.4 }}
        viewport={{ once: true }}
      >
        <p>
          Passionate about software development, problem-solving, and always eager to learn new technologies and contribute to meaningful projects.
        </p>
      </motion.div>
    </section>
  );
};

export default About;
