import React, { useState } from 'react';
import { motion } from 'framer-motion';
import {
  FaHome, FaUser, FaGraduationCap,
  FaProjectDiagram, FaEnvelope
} from 'react-icons/fa';
import { Link as ScrollLink } from 'react-scroll';

const navLinks = [
  { name: 'Home', icon: <FaHome />, to: 'hero' },
  { name: 'About', icon: <FaUser />, to: 'about' },
  { name: 'Education', icon: <FaGraduationCap />, to: 'education' },
  { name: 'Projects', icon: <FaProjectDiagram />, to: 'projects' },
  { name: 'Contact', icon: <FaEnvelope />, to: 'contact' },
];

const Navbar = () => {
  const [activeLink, setActiveLink] = useState('Home');

  const handleSetActive = (to) => {
    setActiveLink(to.charAt(0).toUpperCase() + to.slice(1));
  };

  return (
    <motion.nav
      className="fixed top-4 left-1/2 transform -translate-x-1/2 z-50 bg-white/10 backdrop-blur-xl border border-white/20 shadow-lg rounded-full px-6 py-3 flex items-center space-x-6 sm:space-x-8 text-white"
      initial={{ y: -60, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.6 }}
    >
      {navLinks.map(({ name, icon, to }) => (
        <ScrollLink
          key={name}
          to={to}
          smooth
          duration={500}
          offset={-100}
          onSetActive={handleSetActive}
          className={`flex items-center gap-1 cursor-pointer transition duration-200 ${
            activeLink === name ? 'text-yellow-300' : 'hover:text-yellow-300'
          }`}
        >
          <span className="text-lg">{icon}</span>
          <span className="hidden sm:inline">{name}</span>
        </ScrollLink>
      ))}
    </motion.nav>
  );
};

export default Navbar;
