// ✅ Advanced Navbar.jsx with Icons, Active Link Highlight, Smooth Scroll, and Framer Motion
import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { FaHome, FaUser, FaGraduationCap, FaProjectDiagram, FaEnvelope } from 'react-icons/fa';

const navLinks = [
  { name: 'Home', icon: <FaHome />, href: '#home' },
  { name: 'About', icon: <FaUser />, href: '#about' },
  { name: 'Education', icon: <FaGraduationCap />, href: '#education' },
  { name: 'Projects', icon: <FaProjectDiagram />, href: '#projects' },
  { name: 'Contact', icon: <FaEnvelope />, href: '#contact' },
];

const Navbar = () => {
  const [activeLink, setActiveLink] = useState('Home');

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const offsets = navLinks.map(({ href }) => {
        const el = document.querySelector(href);
        return el ? el.offsetTop - 100 : 0;
      });

      for (let i = offsets.length - 1; i >= 0; i--) {
        if (scrollPosition >= offsets[i]) {
          setActiveLink(navLinks[i].name);
          break;
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleClick = (name, href) => {
    setActiveLink(name);
    document.querySelector(href)?.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <motion.nav
      className="flex justify-between items-center p-4 text-white fixed top-0 w-full z-50 bg-dark/80 backdrop-blur border-b border-gray-800"
      initial={{ y: -60, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.6, ease: 'easeOut' }}
    >
      <motion.h1
        className="text-xl font-bold text-primary"
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ delay: 0.4 }}
      >
        Kommana Roshitha
      </motion.h1>

      <motion.ul
        className="flex gap-6 text-sm md:text-base"
        initial="hidden"
        animate="visible"
        variants={{
          hidden: {},
          visible: {
            transition: {
              staggerChildren: 0.1
            }
          }
        }}
      >
        {navLinks.map(({ name, icon, href }) => (
          <motion.li
            key={name}
            className={`relative flex items-center gap-1 cursor-pointer transition-all duration-200 ${
              activeLink === name ? 'text-primary' : 'text-white hover:text-primary'
            }`}
            onClick={() => handleClick(name, href)}
            variants={{
              hidden: { opacity: 0, y: -10 },
              visible: { opacity: 1, y: 0 }
            }}
            whileHover={{ scale: 1.05 }}
          >
            <span className="text-lg">{icon}</span>
            {name}
            {activeLink === name && (
              <motion.span
                layoutId="underline"
                className="absolute -bottom-1 left-0 right-0 h-0.5 bg-primary rounded-full"
              />
            )}
          </motion.li>
        ))}
      </motion.ul>
    </motion.nav>
  );
};

export default Navbar;
