import React from 'react';
import { BadgeCheck, Code, Database, Settings, BrainCircuit } from 'lucide-react';

const skillsData = [
  {
    title: 'Languages',
    icon: <Code className="w-5 h-5 text-purple-500" />,
    items: ['Java', 'Python', 'C++', 'C', 'HTML', 'CSS', 'JavaScript', 'Tailwind CSS', 'React.js'],
  },
  {
    title: 'Backend & Databases',
    icon: <Database className="w-5 h-5 text-green-500" />,
    items: ['Firebase', 'MongoDB', 'MySQL'],
  },
  {
    title: 'Tools & Libraries',
    icon: <Settings className="w-5 h-5 text-yellow-500" />,
    items: ['OpenCV', 'Tesseract OCR', 'Git', 'VS Code'],
  },
  {
    title: 'Concepts',
    icon: <BrainCircuit className="w-5 h-5 text-blue-500" />,
    items: ['OOP', 'DBMS', 'Data Structures', 'Machine Learning', 'Big Data', 'UI/UX Principles'],
  },
];

const Skills = () => {
  return (
    <section id="skills" className="bg-[#0b0f19] py-20 px-4 text-white max-w-6xl mx-auto">
      <h2 className="text-4xl font-bold text-center text-purple-400 mb-16">Skills</h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 gap-8">
        {skillsData.map(({ title, icon, items }, index) => (
          <div
            key={index}
            className="bg-[#111827] rounded-xl p-6 shadow-md hover:shadow-purple-400/30 transition-all duration-300"
          >
            <div className="flex items-center gap-2 mb-4">
              {icon}
              <h3 className="text-xl font-semibold text-white">{title}</h3>
            </div>
            <ul className="flex flex-wrap gap-2">
              {items.map((skill, idx) => (
                <li
                  key={idx}
                  className="bg-purple-900/40 text-sm px-3 py-1 rounded-full text-white border border-purple-600"
                >
                  {skill}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Skills;
