import React, { useState, useRef } from 'react';
import { motion } from 'framer-motion';
import {
  FaEnvelope,
  FaPaperPlane,
} from 'react-icons/fa';
import emailjs from '@emailjs/browser';

const Contact = () => {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [showPopup, setShowPopup] = useState(false);
  const formRef = useRef();

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    emailjs.sendForm(
      'service_ln1b367',
      'template_wgcza8i',
      formRef.current,
      'T-hfPbdDWJ4vL7fyq'
    )
    .then(() => {
      setShowPopup(true);
      setFormData({ name: '', email: '', message: '' });
    })
    .catch((err) => console.error('FAILED...', err));
  };

  const handlePopupClose = () => setShowPopup(false);

  return (
    <section id="contact" className="px-6 py-20 bg-transparent text-gray-900">
      {/* Section Heading */}
      <motion.h2
        className="text-4xl font-bold text-center text-black mb-16"
        initial={{ opacity: 0, y: -20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        Contact Me
      </motion.h2>

      <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-12">
        {/* Left: Get in Touch Form (Narrower) */}
        <motion.div
          className="bg-white/20 backdrop-blur-md p-8 rounded-2xl shadow-md border border-gray-200 max-w-[calc(100%-3cm)] mx-auto"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          style={{ maxWidth: 'calc(100% - 3cm)' }} // about 3cm less width
        >
          <h3 className="text-2xl font-semibold text-black-700 mb-4">Get in Touch with Me</h3>
          <p className="text-gray-700 mb-6">
            Fill out the form below and I'll get back to you as soon as possible.
          </p>
          <form ref={formRef} onSubmit={handleSubmit} className="space-y-4">
            <input
              type="text"
              name="name"
              placeholder="Your Name"
              required
              className="w-full p-3 rounded-lg bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500"
              value={formData.name}
              onChange={handleChange}
            />
            <input
              type="email"
              name="email"
              placeholder="Your Email"
              required
              className="w-full p-3 rounded-lg bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500"
              value={formData.email}
              onChange={handleChange}
            />
            <textarea
              name="message"
              rows="4"
              placeholder="Your Message"
              required
              className="w-full p-3 rounded-lg bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500"
              value={formData.message}
              onChange={handleChange}
            ></textarea>
          </form>
        </motion.div>

        {/* Right: Contact Cards */}
        <motion.div
          className="flex flex-col gap-6"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          <ContactCard
            icon={<FaEnvelope className="text-3xl text-red-500" />}
            title="Email"
            link="mailto:roshithakommana18@gmail.com"
            value="roshithakommana18@gmail.com"
          />
          <SendMessageCard />
        </motion.div>
      </div>

      {/* Popup */}
      {showPopup && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="backdrop-blur-md bg-white/10 text-white p-6 rounded-xl border border-white/20">
            <h4 className="text-xl mb-4">Message Sent Successfully!</h4>
            <button
              onClick={handlePopupClose}
              className="bg-purple-600 px-4 py-2 rounded hover:bg-purple-700"
            >
              OK
            </button>
          </div>
        </div>
      )}
    </section>
  );
};

// Contact Card Component
const ContactCard = ({ icon, title, link, value }) => {
  const cardVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.4 } },
    hover: { scale: 1.02, y: -2, transition: { type: 'spring', stiffness: 300 } },
  };

  return (
    <motion.a
      href={link}
      target="_blank"
      rel="noopener noreferrer"
      variants={cardVariants}
      initial="hidden"
      whileInView="visible"
      whileHover="hover"
      viewport={{ once: true, amount: 0.2 }}
      className="bg-white/20 backdrop-blur-md p-6 rounded-xl border border-gray-300 shadow-sm flex flex-col items-center justify-center gap-3 transition
                 w-48 h-48 text-center"
      style={{ borderRadius: '1rem' }} // rounded corners
    >
      <div className="text-purple-700">{icon}</div>
      <div>
        <h4 className="text-sm font-semibold text-gray-900">{title}</h4>
        <p className="text-xs text-gray-700 break-words">{value}</p>
      </div>
    </motion.a>
  );
};

// New Send Message Card (button only)
const SendMessageCard = () => {
  return (
    <motion.div
      className="bg-white/20 backdrop-blur-md p-6 rounded-xl border border-gray-300 shadow-sm flex items-center justify-center transition w-48 h-48"
      style={{ borderRadius: '1rem' }}
      initial={{ opacity: 0, y: 20 }}
      whileInView={{ opacity: 1, y: 0 }}
      whileHover={{ scale: 1.02, y: -2, transition: { type: 'spring', stiffness: 300 } }}
      viewport={{ once: true, amount: 0.2 }}
    >
      <button
        onClick={() => {
          document.getElementById('contact').scrollIntoView({ behavior: 'smooth' });
        }}
        className="flex items-center gap-2 bg-purple-600 hover:bg-purple-700 text-white font-semibold px-5 py-3 rounded-lg"
      >
        <FaPaperPlane />
        Send a Message
      </button>
    </motion.div>
  );
};

export default Contact;
