import React from 'react';
import { FaGithub, FaLinkedin, FaInstagram } from 'react-icons/fa';
import { motion } from 'framer-motion';

const Hero = () => {
  return (
    <section className="text-white min-h-[90vh] flex items-center justify-center px-8">
      <div className="max-w-6xl grid md:grid-cols-2 gap-12">
        <motion.div
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
        >
          <h1 className="text-5xl font-bold leading-tight mb-4">
            <span className="text-primary">Web Developer</span><br />
            & UI/UX Designer
          </h1>
          <p className="text-lg text-gray-300 mb-6">
            Passionate and goal-oriented Computer Science student with expertise in Web Dev, AI, ML, and UI/UX Design.
          </p>

          <div className="flex gap-3 flex-wrap mb-4">
            {['Java', 'Python', 'React', 'Firebase', 'Tailwind'].map(skill => (
              <span key={skill} className="px-3 py-1 bg-gray-800 rounded-full text-sm">{skill}</span>
            ))}
          </div>

          <div className="flex gap-4 mb-6">
            <a href="#projects" className="bg-primary px-6 py-2 rounded shadow">Projects</a>
            <a href="#contact" className="border border-primary px-6 py-2 rounded">Contact</a>
          </div>

          <div className="flex gap-6 text-2xl">
            <a href="https://github.com/Roshitha-Kommana"><FaGithub /></a>
            <a href="https://linkedin.com/in/roshitha-kommana"><FaLinkedin /></a>
            <a href="https://instagram.com"><FaInstagram /></a>
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
        >
          <img src="/profile.jpg" alt="Profile" className="rounded-lg shadow-xl" />
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
