// About.jsx
import React from 'react';
import { motion } from 'framer-motion';

const About = () => {
  return (
    <motion.section
      id="about"
      className="text-white px-8 py-16"
      initial={{ opacity: 0, y: 50 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
      viewport={{ once: true }}
    >
      <h2 className="text-3xl font-bold text-primary mb-4">About Me</h2>
      <p className="text-gray-300 max-w-2xl">
        I am a passionate and dedicated Computer Science student with a deep interest in web development,
        UI/UX design, and AI/ML.
      </p>
    </motion.section>
  );
};

export default About;
