import React from 'react';
import { motion } from 'framer-motion';
import { SiJava, SiFirebase, SiOpenvpn } from 'react-icons/si';
import { FaCheckCircle } from 'react-icons/fa';

const skillsData = [
  {
    category: 'Languages',
    icon: <SiJava size={24} className="text-purple-500" />,
    skills: ['Java', 'Python', 'C++', 'C', 'HTML', 'CSS', 'JavaScript', 'Tailwind CSS', 'React.js'],
  },
  {
    category: 'Backend & Databases',
    icon: <SiFirebase size={24} className="text-purple-500" />,
    skills: ['Firebase', 'MongoDB', 'MySQL'],
  },
  {
    category: 'Tools & Libraries',
    icon: <SiOpenvpn size={24} className="text-purple-500" />,
    skills: ['OpenCV', 'Tesseract OCR', 'Git', 'VS Code'],
  },
  {
    category: 'Concepts',
    icon: <SiJava size={24} className="text-purple-500" />, // Reused icon for concepts, change if you want
    skills: ['OOP', 'DBMS', 'Data Structures', 'Machine Learning', 'Big Data', 'UI/UX Principles'],
  },
];

const Skills = () => {
  return (
    <section
      id="skills"
      className="max-w-6xl mx-auto py-20 px-4 bg-[#0b0f19] text-white"
    >
      <h2 className="text-4xl font-bold mb-16 text-center text-purple-400">Skills</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        {skillsData.map(({ category, icon, skills }, index) => (
          <motion.div
            key={category}
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true, amount: 0.6 }}
            transition={{ duration: 0.5, delay: index * 0.2 }}
            className="bg-[#111827] rounded-lg p-6 shadow-md hover:shadow-purple-400/40"
          >
            <div className="flex items-center mb-4 space-x-3">
              {icon}
              <h3 className="text-xl font-semibold text-purple-400">{category}</h3>
            </div>

            <div className="flex flex-wrap gap-3">
              {skills.map((skill) => (
                <span
                  key={skill}
                  className="flex items-center space-x-1 bg-purple-700/20 text-purple-300 px-3 py-1 rounded-full text-sm font-medium select-none"
                >
                  <FaCheckCircle className="text-purple-500" />
                  <span>{skill}</span>
                </span>
              ))}
            </div>
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default Skills;
