import React from 'react';
import { motion } from 'framer-motion';

import { DiCode } from 'react-icons/di';
import { FaDatabase, FaCheckCircle } from 'react-icons/fa';
import { FiTool } from 'react-icons/fi';
import { MdLightbulb } from 'react-icons/md';
import { GiSkills } from 'react-icons/gi';

const skillsData = [
  {
    category: 'Languages',
    icon: <DiCode size={20} className="text-purple-500" />,
    skills: ['Java', 'Python', 'C++', 'C', 'HTML', 'CSS', 'JavaScript', 'Tailwind CSS', 'React.js'],
  },
  {
    category: 'Backend & Databases',
    icon: <FaDatabase size={20} className="text-purple-500" />,
    skills: ['Firebase', 'MongoDB', 'MySQL'],
  },
  {
    category: 'Tools & Libraries',
    icon: <FiTool size={20} className="text-purple-500" />,
    skills: ['OpenCV', 'Tesseract OCR', 'Git', 'VS Code'],
  },
  {
    category: 'Concepts',
    icon: <MdLightbulb size={20} className="text-purple-500" />,
    skills: ['OOP', 'DBMS', 'Data Structures', 'Machine Learning', 'Big Data', 'UI/UX Principles'],
  },
  {
    category: 'Key Strengths',
    icon: <GiSkills size={20} className="text-purple-500" />,
    skills: ['Problem Solving', 'Team Collaboration', 'Active Listener', 'Time Management'],
  },
];

const containerVariant = {
  hidden: {},
  visible: {
    transition: {
      staggerChildren: 0.1,
    },
  },
};

const cardVariant = {
  hidden: { opacity: 0, y: 12, scale: 0.95 },
  visible: { opacity: 1, y: 0, scale: 1, transition: { duration: 0.3, ease: 'easeOut' } },
  hover: { scale: 1.03, boxShadow: '0 6px 18px rgba(124, 58, 237, 0.4)' },
};

const skillVariant = {
  hidden: { opacity: 0, x: -8 },
  visible: { opacity: 1, x: 0, transition: { duration: 0.25 } },
};

const Skills = () => {
  return (
    <section id="skills" className="max-w-5xl mx-auto py-12 px-4 bg-[#0b0f19] text-white select-none">
      <h2 className="text-3xl font-extrabold mb-12 text-center text-purple-400 tracking-wide">
        Skills & Strengths
      </h2>

      <motion.div
        className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6"
        variants={containerVariant}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true, amount: 0.4 }}
      >
        {skillsData.map(({ category, icon, skills }) => (
          <motion.div
            key={category}
            variants={cardVariant}
            whileHover="hover"
            className="bg-[#1e2235] rounded-lg p-5 shadow-md cursor-pointer border border-transparent hover:border-purple-600 transition-all"
          >
            <div className="flex items-center mb-4 space-x-3">
              <div className="p-2 bg-purple-600 rounded-full flex justify-center items-center text-white shadow-md">
                {icon}
              </div>
              <h3 className="text-xl font-semibold text-purple-300 tracking-wide">{category}</h3>
            </div>

            <motion.div className="flex flex-wrap gap-2" variants={containerVariant}>
              {skills.map((skill) => (
                <motion.span
                  key={skill}
                  variants={skillVariant}
                  className="flex items-center space-x-1 bg-purple-800/40 text-purple-200 px-3 py-0.5 rounded-full text-xs font-medium shadow-sm"
                >
                  <FaCheckCircle className="text-purple-400" size={12} />
                  <span>{skill}</span>
                </motion.span>
              ))}
            </motion.div>
          </motion.div>
        ))}
      </motion.div>
    </section>
  );
};

export default Skills;
