import React from 'react';
import { useNavigate, useParams } from 'react-router-dom';

// React icons
import { FaArrowLeft, FaExternalLinkAlt, FaGithub, FaTools, FaCheckCircle } from 'react-icons/fa';

const projectsData = {
  donateease: {
    title: 'DonateEase – Web-Based Donation Platform',
    duration: 'Dec 2024 - Feb 2025',
    technologies: ['HTML', 'JavaScript', 'Firebase', 'Cloudinary'],
    description:
      'Built a web application enabling users to donate across 4 categories (money, clothes, books, electronics). Integrated QR-based UPI payments and Cloudinary API for secure screenshot storage. Enabled conditional dynamic forms with validations and Firestore-based data persistence. Achieved 100% functional donation tracking and improved submission accuracy.',
    liveDemo: 'https://donateease-2f624.web.app/',
    github: 'https://github.com/Roshitha-Kommana/DonateEase',
    keyFeatures: [
      'Multi-category donations: money, clothes, books, electronics',
      'QR code-based UPI payment integration',
      'Dynamic donation forms with validations',
      'Secure screenshot upload with Cloudinary',
      'Firestore for donation data persistence',
      'Real-time donation tracking',
    ],
    imageUrl: '/assets/donate.png',
  },

  smartocr: {
    title: 'Smart OCR – Automated Text Extraction System',
    duration: 'July 2024 - Oct 2024',
    technologies: ['Python', 'OpenCV', 'PyMuPDF', 'Tesseract'],
    description:
      'Built an OCR pipeline to extract text from scanned PDFs and images. Applied preprocessing (grayscale, denoising, thresholding) to improve accuracy. Used Tesseract to convert images into structured text for document digitization.',
    liveDemo: null,
    github: 'https://github.com/Roshitha-Kommana/smart_ocr',
    keyFeatures: [
      'Automated text extraction from scanned documents',
      'Image preprocessing to enhance OCR accuracy',
      'Support for PDFs and images',
      'Structured text output for easy document digitization',
    ],
    imageUrl:
      'https://raw.githubusercontent.com/Roshitha-Kommana/smart_ocr/main/screenshot.png',
  },
};

const ProjectDetails = () => {
  const navigate = useNavigate();
  const { id } = useParams();

  const project = projectsData[id];

  if (!project) {
    return (
      <div className="min-h-screen flex flex-col justify-center items-center bg-gradient-to-b from-gray-900 to-gray-800 text-white px-4">
        <p className="text-2xl font-semibold mb-4">Project not found.</p>
        <button
          onClick={() => navigate(-1)}
          className="flex items-center gap-2 text-purple-400 hover:text-purple-600 font-semibold transition"
        >
          <FaArrowLeft /> Go Back
        </button>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-[#0f172a] to-[#1e293b] px-4 py-10 flex justify-center">
      <div className="max-w-4xl w-full bg-gray-900 rounded-xl shadow-2xl overflow-hidden text-white">
        {/* Header and Back Button */}
        <div className="px-6 py-5 flex items-center border-b border-gray-700">
          <button
            onClick={() => navigate(-1)}
            className="flex items-center gap-2 text-purple-400 hover:text-purple-600 transition font-semibold"
            aria-label="Go Back"
          >
            <FaArrowLeft size={18} /> Back
          </button>
        </div>

        {/* Image Section */}
        <div className="relative h-64 md:h-80 bg-gradient-to-tr from-purple-900 via-purple-700 to-purple-900 flex justify-center items-center overflow-hidden">
          <img
            src={project.imageUrl}
            alt={`${project.title} Screenshot`}
            className="max-h-full max-w-full object-contain rounded-md shadow-lg"
            loading="lazy"
          />
          <div className="absolute inset-0 bg-black bg-opacity-30 pointer-events-none rounded-md" />
        </div>

        {/* Content Section */}
        <div className="px-8 py-8">
          <h1 className="text-4xl font-extrabold text-purple-300 mb-2 flex items-center gap-3">
            <FaTools /> {project.title}
          </h1>
          <p className="text-sm text-purple-500 mb-6 italic">{project.duration}</p>

          <p className="text-gray-300 leading-relaxed mb-8">{project.description}</p>

          {/* Key Features */}
          <section className="mb-10">
            <h2 className="text-2xl font-semibold text-purple-400 mb-4 flex items-center gap-3">
              <FaCheckCircle /> Key Features
            </h2>
            <ul className="list-disc list-inside space-y-2 text-gray-300">
              {project.keyFeatures.map((feature, index) => (
                <li
                  key={index}
                  className="flex items-center gap-3 text-gray-200 hover:text-purple-400 transition"
                >
                  <FaCheckCircle className="text-purple-500 flex-shrink-0" />
                  {feature}
                </li>
              ))}
            </ul>
          </section>

          {/* Technologies */}
          <section className="mb-10">
            <h2 className="text-2xl font-semibold text-purple-400 mb-4 flex items-center gap-3">
              <FaTools /> Technologies Used
            </h2>
            <div className="flex flex-wrap gap-3">
              {project.technologies.map((tech, index) => (
                <span
                  key={index}
                  className="bg-purple-700 text-purple-200 px-4 py-1 rounded-full text-sm font-semibold flex items-center gap-2 shadow"
                >
                  <FaTools /> {tech}
                </span>
              ))}
            </div>
          </section>

          {/* Links */}
          <div className="flex flex-wrap gap-6 justify-center md:justify-start">
            {project.liveDemo && (
              <a
                href={project.liveDemo}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-flex items-center gap-2 bg-purple-600 hover:bg-purple-700 px-6 py-3 rounded-lg font-semibold transition text-white shadow-lg"
                aria-label="Live Demo"
              >
                Live Demo <FaExternalLinkAlt />
              </a>
            )}
            <a
              href={project.github}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-flex items-center gap-2 border-2 border-purple-600 hover:border-purple-700 px-6 py-3 rounded-lg font-semibold transition text-purple-300 hover:text-purple-400 shadow-lg"
              aria-label="GitHub Repository"
            >
              GitHub <FaGithub />
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProjectDetails;
