import React from 'react';
import { motion } from 'framer-motion';

const educationData = [
  {
    id: 1,
    institute: 'Maharaj Vijayaram Gajapathi Raj (MVGR) College of Engineering',
    course: 'B.Tech - CSE',
    year: '2022 - 2026',
    score: 'CGPA: 8.53',
  },
  {
    id: 2,
    institute: 'Sri Chaitanya Junior College',
    course: 'Intermediate - MPC',
    year: '2020 - 2022',
    score: '74.8%',
  },
  {
    id: 3,
    institute: 'Sri Chaitanya Techno School',
    course: 'SSC',
    year: '2019 - 2020',
    score: '99.8%',
  },
];

const Education = () => {
  return (
    <section id="education" className="bg-[#0f172a] text-white py-20 px-6">
      <h2 className="text-3xl font-bold text-center text-cyan-400 mb-12">Education</h2>
      <div className="relative max-w-5xl mx-auto">
        {/* Center timeline line */}
        <div className="absolute left-1/2 transform -translate-x-1/2 h-full w-1 bg-cyan-400"></div>

        {educationData.map((edu, index) => {
          const isLeft = index % 2 === 0;

          return (
            <motion.div
              key={edu.id}
              className={`w-full flex mb-16 items-center ${isLeft ? 'justify-start' : 'justify-end'}`}
              initial={{ opacity: 0, x: isLeft ? -100 : 100 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5 }}
            >
              <div className="relative bg-gray-800 rounded-lg shadow-lg w-64 p-4">
                <h3 className="text-lg font-semibold text-cyan-300">{edu.course}</h3>
                <p className="text-gray-300">{edu.institute}</p>
                <p className="text-sm italic text-gray-400">{edu.year}</p>
                <p className="text-sm text-white font-bold">{edu.score}</p>
                <span className="absolute top-1/2 -translate-y-1/2 w-4 h-4 bg-cyan-400 rounded-full border-4 border-[#0f172a] left-full -ml-2"></span>
              </div>
            </motion.div>
          );
        })}
      </div>
    </section>
  );
};

export default Education;
