import React, { useState, useRef } from 'react';
import { motion } from 'framer-motion';
import emailjs from '@emailjs/browser';
import { FaGithub, FaLinkedin, FaEnvelope, FaPhoneAlt, FaMapMarkerAlt } from 'react-icons/fa';

const Contact = () => {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [showPopup, setShowPopup] = useState(false);
  const formRef = useRef();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    emailjs.sendForm(
      'service_ln1b367',
      'template_wgcza8i',
      formRef.current,
      'T-hfPbdDWJ4vL7fyq'
    )
    .then(() => {
      setShowPopup(true);
      setFormData({ name: '', email: '', message: '' });
    })
    .catch((err) => console.error('FAILED...', err));
  };

  return (
    <section id="contact" className="px-6 py-20 text-gray-900">
      <motion.h2
        className="text-4xl font-bold mb-12 text-center text-purple-700"
        initial={{ opacity: 0, y: -30 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        Contact Me
      </motion.h2>

      <div className="max-w-6xl mx-auto grid md:grid-cols-2 gap-12">
        {/* Left Side - Form */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          <h3 className="text-xl font-semibold mb-4 text-gray-800">Send me a message</h3>
          <p className="mb-6 text-sm text-gray-600">
            Fill out the form below and I'll get back to you as soon as possible.
          </p>
          <form ref={formRef} onSubmit={handleSubmit} className="space-y-4">
            <input
              type="text"
              name="name"
              placeholder="Your Full Name"
              required
              className="w-full p-3 rounded bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-400"
              value={formData.name}
              onChange={handleChange}
            />
            <input
              type="email"
              name="email"
              placeholder="Your Email"
              required
              className="w-full p-3 rounded bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-400"
              value={formData.email}
              onChange={handleChange}
            />
            <textarea
              name="message"
              rows="5"
              placeholder="Your Message"
              required
              className="w-full p-3 rounded bg-white border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-400"
              value={formData.message}
              onChange={handleChange}
            ></textarea>
            <button type="submit" className="bg-purple-600 text-white px-6 py-3 rounded shadow hover:scale-105 transition-transform">
              Send Message
            </button>
          </form>
        </motion.div>

        {/* Right Side - Contact Details */}
        <motion.div
          className="flex flex-col gap-6"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.3 }}
          viewport={{ once: true }}
        >
          <h3 className="text-xl font-semibold mb-4 text-gray-800">Connect with me</h3>
          <div className="space-y-4 text-sm text-gray-700">
            <div className="flex items-center gap-3">
              <FaGithub className="text-purple-600" />
              <a href="https://github.com/Roshitha-Kommana" target="_blank" rel="noopener noreferrer">
                github.com/Roshitha-Kommana
              </a>
            </div>
            <div className="flex items-center gap-3">
              <FaLinkedin className="text-purple-600" />
              <a href="https://linkedin.com/in/roshitha-kommana" target="_blank" rel="noopener noreferrer">
                linkedin.com/in/roshitha-kommana
              </a>
            </div>
            <div className="flex items-center gap-3">
              <FaEnvelope className="text-purple-600" />
              <a href="mailto:roshithakommana18@gmail.com">
                roshithakommana18@gmail.com
              </a>
            </div>
            <div className="flex items-center gap-3">
              <FaPhoneAlt className="text-purple-600" />
              <span>+91 63026 80957</span>
            </div>
            <div className="flex items-center gap-3">
              <FaMapMarkerAlt className="text-purple-600" />
              <span>Vizianagaram, Andhra Pradesh, India</span>
            </div>
          </div>
        </motion.div>
      </div>

      {/* Popup */}
      {showPopup && (
        <div className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-50 z-50">
          <div className="backdrop-blur-md bg-white/10 text-white p-8 rounded-xl shadow-lg border border-white/20">
            <h3 className="text-xl font-semibold mb-4">Sent Successfully!</h3>
            <button
              onClick={() => setShowPopup(false)}
              className="bg-purple-600 px-4 py-2 rounded hover:bg-purple-700"
            >
              OK
            </button>
          </div>
        </div>
      )}
    </section>
  );
};

export default Contact;
