import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const projects = [
  {
    id: 'donateease',
    name: 'DonateEase – Web-Based Donation Platform',
    description:
      'Built a web application enabling users to donate across 4 categories (money, clothes, books, electronics). Integrated QR-based UPI payments and Cloudinary API for secure screenshot storage. Enabled conditional dynamic forms with validations and Firestore-based data persistence. Achieved 100% functional donation tracking and improved submission accuracy.',
    date: 'Dec 2024 - Feb 2025',
    technologies: ['HTML', 'JavaScript', 'Firebase', 'Cloudinary'],
    keyFeatures: [
      'Built a web application enabling users to donate across 4 categories (money, clothes, books, electronics)',
      'Integrated QR-based UPI payments and Cloudinary API for secure screenshot storage',
      'Enabled conditional dynamic forms with validations and Firestore-based data persistence',
      'Achieved 100% functional donation tracking and improved submission accuracy',
    ],
    liveDemo: 'https://donateease-2f624.web.app/',
    github: 'https://github.com/Roshitha-Kommana/DonateEase',
  },
  {
    id: 'smartocr',
    name: 'Smart OCR – Automated Text Extraction System',
    description:
      'Built an OCR pipeline to extract text from scanned PDFs and images. Applied preprocessing (grayscale, denoising, thresholding) to improve accuracy. Used Tesseract to convert images into structured text for document digitization.',
    date: 'N/A',
    technologies: ['Python', 'OpenCV', 'PyMuPDF', 'Tesseract'],
    keyFeatures: [
      'Built an OCR pipeline to extract text from scanned PDFs and images',
      'Applied preprocessing (grayscale, denoising, thresholding) to improve accuracy',
      'Used Tesseract to convert images into structured text for document digitization',
    ],
    liveDemo: null,
    github: 'https://github.com/Roshitha-Kommana/smart_ocr',
  },
];

const ProjectDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const project = projects.find((p) => p.id === id);

  if (!project) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-[#0b0f19] text-white">
        <div className="text-center">
          <h2 className="text-3xl font-bold mb-6">Project Not Found</h2>
          <button
            onClick={() => navigate(-1)}
            className="px-6 py-2 rounded-lg bg-purple-700 hover:bg-purple-600 transition"
          >
            Go Back
          </button>
        </div>
      </div>
    );
  }

  return (
    <main className="min-h-screen bg-[#0b0f19] text-white max-w-4xl mx-auto p-8">
      <button
        onClick={() => navigate(-1)}
        className="mb-6 px-4 py-2 rounded-md bg-purple-700 hover:bg-purple-600 transition"
      >
        &larr; Back
      </button>

      <h1 className="text-4xl font-extrabold mb-4 text-purple-400">{project.name}</h1>
      <p className="text-purple-300 italic mb-4">{project.date}</p>

      <p className="mb-6">{project.description}</p>

      <h2 className="text-2xl font-semibold mb-2 text-purple-300">Key Features</h2>
      <ul className="list-disc list-inside mb-6 text-purple-200">
        {project.keyFeatures.map((feat, idx) => (
          <li key={idx}>{feat}</li>
        ))}
      </ul>

      <h2 className="text-2xl font-semibold mb-2 text-purple-300">Technologies Used</h2>
      <ul className="flex flex-wrap gap-3 mb-8">
        {project.technologies.map((tech) => (
          <li
            key={tech}
            className="bg-purple-700 text-purple-100 rounded-full px-4 py-1 text-sm font-semibold"
          >
            {tech}
          </li>
        ))}
      </ul>

      <div className="flex gap-6">
        {project.liveDemo ? (
          <a
            href={project.liveDemo}
            target="_blank"
            rel="noopener noreferrer"
            className="px-6 py-2 bg-purple-600 rounded-lg font-semibold hover:bg-purple-500 transition"
          >
            Live Demo
          </a>
        ) : (
          <span className="px-6 py-2 bg-purple-800 rounded-lg font-semibold opacity-50 cursor-not-allowed">
            No Live Demo
          </span>
        )}
        <a
          href={project.github}
          target="_blank"
          rel="noopener noreferrer"
          className="px-6 py-2 bg-purple-600 rounded-lg font-semibold hover:bg-purple-500 transition"
        >
          GitHub Repo
        </a>
      </div>
    </main>
  );
};

export default ProjectDetails;
