import React from 'react';
import { useNavigate } from 'react-router-dom';

// Import icons from react-icons
import { FaArrowLeft, FaExternalLinkAlt, FaGithub, FaTools, FaCheckCircle } from 'react-icons/fa';

const ProjectDetails = () => {
  const navigate = useNavigate();

  const project = {
    title: 'DonateEase – Web-Based Donation Platform',
    duration: 'Dec 2024 - Feb 2025',
    technologies: ['HTML', 'JavaScript', 'Firebase', 'Cloudinary'],
    description:
      'Built a web application enabling users to donate across 4 categories (money, clothes, books, electronics). Integrated QR-based UPI payments and Cloudinary API for secure screenshot storage. Enabled conditional dynamic forms with validations and Firestore-based data persistence. Achieved 100% functional donation tracking and improved submission accuracy.',
    liveDemo: 'https://donateease-2f624.web.app/',
    github: 'https://github.com/Roshitha-Kommana/DonateEase',
    keyFeatures: [
      'Multi-category donations: money, clothes, books, electronics',
      'QR code-based UPI payment integration',
      'Dynamic donation forms with validations',
      'Secure screenshot upload with Cloudinary',
      'Firestore for donation data persistence',
      'Real-time donation tracking',
    ],
    imageUrl: 'https://user-images.githubusercontent.com/your-image-path/donateease-screenshot.png', // Replace with your image URL
  };

  return (
    <div className="pt-20 px-6 max-w-3xl mx-auto bg-[#0b0f19] min-h-screen text-white rounded-lg shadow-lg">
      {/* Back button with icon */}
      <button
        onClick={() => navigate(-1)}
        className="mb-6 text-purple-400 hover:text-purple-600 font-semibold flex items-center gap-2 text-lg"
      >
        <FaArrowLeft /> Back
      </button>

      {/* Project Image */}
      <div className="w-full h-56 mb-6 overflow-hidden rounded-md shadow-md">
        <img
          src={project.imageUrl}
          alt={`${project.title} Screenshot`}
          className="w-full h-full object-contain bg-gray-900"
        />
      </div>

      <h1 className="text-3xl font-bold mb-2 text-purple-300 flex items-center gap-3">
        <FaTools /> {project.title}
      </h1>
      <p className="text-sm text-purple-500 mb-6 italic">{project.duration}</p>

      <p className="mb-6">{project.description}</p>

      <h2 className="text-xl font-semibold text-purple-400 mb-3 flex items-center gap-2">
        <FaCheckCircle /> Key Features
      </h2>
      <ul className="list-disc list-inside mb-6 space-y-1">
        {project.keyFeatures.map((feature, index) => (
          <li key={index} className="flex items-center gap-2">
            <FaCheckCircle className="text-purple-500" />
            {feature}
          </li>
        ))}
      </ul>

      <h2 className="text-xl font-semibold text-purple-400 mb-3 flex items-center gap-2">
        <FaTools /> Technologies Used
      </h2>
      <div className="flex flex-wrap gap-2 mb-6">
        {project.technologies.map((tech, index) => (
          <span
            key={index}
            className="bg-purple-700 text-purple-200 px-3 py-1 rounded-full text-sm font-medium flex items-center gap-1"
          >
            <FaTools /> {tech}
          </span>
        ))}
      </div>

      <div className="flex flex-wrap gap-4 mb-10">
        <a
          href={project.liveDemo}
          target="_blank"
          rel="noopener noreferrer"
          className="flex-1 min-w-[120px] text-center bg-purple-600 hover:bg-purple-700 px-5 py-2 rounded-lg font-semibold transition flex items-center justify-center gap-2"
        >
          Live Demo <FaExternalLinkAlt />
        </a>
        <a
          href={project.github}
          target="_blank"
          rel="noopener noreferrer"
          className="flex-1 min-w-[120px] text-center border border-purple-600 hover:border-purple-700 px-5 py-2 rounded-lg font-semibold transition flex items-center justify-center gap-2"
        >
          GitHub <FaGithub />
        </a>
      </div>
    </div>
  );
};

export default ProjectDetails;
