// Skills.jsx
import React from 'react';
import { motion } from 'framer-motion';
import { DiCode } from 'react-icons/di';
import { FaDatabase } from 'react-icons/fa';
import { FiTool } from 'react-icons/fi';
import { MdLightbulb } from 'react-icons/md';
import { GiSkills } from 'react-icons/gi';

const skillsData = [
  {
    category: 'Languages',
    icon: <DiCode size={18} className="text-purple-500" />,
    skills: ['Java', 'Python', 'C++', 'C', 'HTML', 'CSS', 'JavaScript', 'Tailwind CSS', 'React.js'],
  },
  {
    category: 'Backend & Databases',
    icon: <FaDatabase size={18} className="text-purple-500" />,
    skills: ['Firebase', 'MongoDB', 'MySQL'],
  },
  {
    category: 'Tools & Libraries',
    icon: <FiTool size={18} className="text-purple-500" />,
    skills: ['OpenCV', 'Tesseract OCR', 'Git', 'VS Code'],
  },
  {
    category: 'Concepts',
    icon: <MdLightbulb size={18} className="text-purple-500" />,
    skills: ['OOP', 'DBMS', 'Data Structures', 'Machine Learning', 'Big Data', 'UI/UX Principles'],
  },
  {
    category: 'Key Strengths',
    icon: <GiSkills size={18} className="text-purple-500" />,
    skills: ['Problem Solving', 'Team Collaboration', 'Active Listener', 'Time Management'],
  },
];

const Skills = () => {
  return (
    <section id="skills" className="text-white px-6 py-16">
      <motion.h2
        className="text-3xl font-bold mb-10 text-primary text-center"
        initial={{ opacity: 0, y: -20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        viewport={{ once: false, amount: 0.3 }}
      >
        Skills
      </motion.h2>

      <motion.div
        className="max-w-5xl mx-auto grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6"
        initial={{ opacity: 0, scale: 0.95 }}
        whileInView={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.8, ease: 'easeOut' }}
        viewport={{ once: false, amount: 0.3 }}
      >
        {skillsData.map((category, idx) => (
          <motion.div
            key={idx}
            className="bg-white/10 backdrop-blur-md rounded-xl shadow-lg p-6 border border-white/20 hover:scale-[1.02] transition-transform duration-300"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: idx * 0.2 }}
            viewport={{ once: false, amount: 0.3 }}
          >
            <div className="flex items-center gap-2 mb-4">
              {category.icon}
              <h3 className="text-lg font-semibold text-white">{category.category}</h3>
            </div>
            <ul className="list-disc list-inside space-y-1 text-gray-200 text-sm">
              {category.skills.map((skill, i) => (
                <li key={i}>{skill}</li>
              ))}
            </ul>
          </motion.div>
        ))}
      </motion.div>
    </section>
  );
};

export default Skills;
