import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-scroll';
import './About.css';  // Import the CSS file for custom styles

const About = () => {
  return (
    <section id="about" className="text-white px-6 py-16 bg-dark max-w-6xl mx-auto">
      {/* Heading */}
      <motion.h2
        className="text-3xl font-bold mb-10 text-primary text-center"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        About Me
      </motion.h2>

      {/* Cards */}
      <div className="flex gap-8 mb-8 flex-wrap justify-center">
        <motion.div
          className="about__card"
          whileHover={{ scale: 1.05 }}
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.1 }}
        >
          <h3 className="text-lg font-semibold mb-2">Final Year Student</h3>
          <p className="text-gray-400">
            Currently pursuing B.Tech in Computer Science, final year.
          </p>
        </motion.div>

        <motion.div
          className="about__card"
          whileHover={{ scale: 1.05 }}
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.3 }}
        >
          <h3 className="text-lg font-semibold mb-2">Projects Completed</h3>
          <p className="text-primary text-3xl font-bold mb-4">10+</p>
          <Link
            to="projects"
            smooth={true}
            duration={500}
            className="cursor-pointer text-primary underline hover:no-underline"
          >
            View Projects
          </Link>
        </motion.div>
      </div>

      {/* Content section */}
      <motion.div
        className="text-gray-300 max-w-4xl mx-auto"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.6, delay: 0.5 }}
      >
        {/* Replace with your content */}
        <p>
          I am a passionate computer science student with experience in web development, machine learning, and cloud computing. I enjoy building projects that solve real-world problems and constantly strive to learn new technologies.
        </p>
      </motion.div>
    </section>
  );
};

export default About;
