// src/components/ProjectsAndCertificates.jsx
import React, { useState } from 'react';
import { FaProjectDiagram, FaCertificate, FaExternalLinkAlt } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';

const projects = [
  {
    id: 1,
    title: 'IntervueAI',
    shortDesc: 'Real-time mock interviews with AI. No forms, just natural conversation.',
    image: 'https://via.placeholder.com/300x150',
    liveDemo: 'https://intervueai.example.com',
    github: 'https://github.com/user/intervueai',
    details: {
      features: [
        'ðŸ” User Auth â€“ Sign up/sign in via Firebase',
        'ðŸŽ›ï¸ Dashboard â€“ Intuitive UI for managing interviews',
        'ðŸ¤– AI Interviews â€“ Mock interviews via Gemini',
        'ðŸ§  AI Personas â€“ Practice with lifelike digital characters',
        'ðŸ“ Live Feedback â€“ Transcription with tips and summaries',
      ],
      technologies: ['Next.js', 'Firebase', 'Shadcn', 'Vapi', 'Gemini']
    }
  },
  // Add more projects here
];

const certificates = [
  'Artificial Intelligence Fundamentals â€“ IBM',
  'AWS Academy Cloud Foundations',
  'Cybersecurity Essentials â€“ Cisco'
];

const ProjectsAndCertificates = () => {
  const [activeTab, setActiveTab] = useState('projects');
  const navigate = useNavigate();

  return (
    <section className="py-20 px-6 bg-[#0b0f19] text-white">
      <div className="max-w-6xl mx-auto">
        <div className="flex justify-center gap-6 mb-12">
          <button
            onClick={() => setActiveTab('projects')}
            className={`backdrop-blur-sm bg-white/10 px-6 py-2 rounded-xl border border-purple-500 hover:shadow-xl flex items-center gap-2 transition-all ${activeTab === 'projects' ? 'bg-purple-600 text-white' : 'text-purple-300'}`}
          >
            <FaProjectDiagram /> Projects
          </button>
          <button
            onClick={() => setActiveTab('certificates')}
            className={`backdrop-blur-sm bg-white/10 px-6 py-2 rounded-xl border border-purple-500 hover:shadow-xl flex items-center gap-2 transition-all ${activeTab === 'certificates' ? 'bg-purple-600 text-white' : 'text-purple-300'}`}
          >
            <FaCertificate /> Certificates
          </button>
        </div>

        {activeTab === 'projects' ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {projects.map((project) => (
              <div key={project.id} className="bg-[#1e2235] rounded-xl p-4 shadow-lg border border-purple-500 hover:scale-105 transition-all">
                <img
                  src={project.image}
                  alt={project.title}
                  className="rounded-md w-full h-40 object-cover mb-4 p-1 bg-white/5"
                />
                <h3 className="text-xl font-semibold text-purple-300 mb-2">{project.title}</h3>
                <p className="text-sm text-purple-100 mb-4">{project.shortDesc}</p>
                <div className="flex justify-between items-center">
                  <a
                    href={project.liveDemo}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-purple-400 hover:underline flex items-center gap-1"
                  >
                    Live Demo <FaExternalLinkAlt size={14} />
                  </a>
                  <button
                    onClick={() => navigate(`/project/${project.id}`)}
                    className="text-sm text-purple-300 hover:underline"
                  >
                    Full Details â†’
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <ul className="space-y-4 text-purple-100">
            {certificates.map((cert, index) => (
              <li key={index} className="bg-[#1e2235] p-4 rounded-xl border border-purple-500 shadow-sm">
                {cert}
              </li>
            ))}
          </ul>
        )}
      </div>
    </section>
  );
};

export default ProjectsAndCertificates;
