import React, { useState, useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import {
  FaHome, FaUser, FaGraduationCap,
  FaProjectDiagram, FaCertificate, FaMedal, FaEnvelope,
  FaChevronDown, FaChevronUp
} from 'react-icons/fa';
import { Link as ScrollLink } from 'react-scroll';

const navLinks = [
  { name: 'Home', icon: <FaHome />, to: 'hero' },
  { name: 'About', icon: <FaUser />, to: 'about' },
  { name: 'Education', icon: <FaGraduationCap />, to: 'education' },
  {
    name: 'Projects',
    icon: <FaProjectDiagram />,
    to: 'projects',
    dropdown: [
      { name: 'Projects', to: 'projects', icon: <FaProjectDiagram /> },
      { name: 'Certificates', to: 'certificates', icon: <FaCertificate /> },
      { name: 'Badges', to: 'badges', icon: <FaMedal /> },
    ]
  },
  { name: 'Contact', icon: <FaEnvelope />, to: 'contact' },
];

const Navbar = () => {
  const [activeLink, setActiveLink] = useState('Home');
  const [showDropdown, setShowDropdown] = useState(false);
  const dropdownRef = useRef(null);

  const handleSetActive = (to) => {
    setActiveLink(to.charAt(0).toUpperCase() + to.slice(1));
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <motion.nav
      className="flex justify-between items-center px-4 py-3 text-white fixed top-0 w-full z-50 bg-dark/80 backdrop-blur-md border-b border-gray-700"
      initial={{ y: -60, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.6 }}
    >
      <motion.h1
        className="text-xl font-bold text-primary"
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ delay: 0.4 }}
      >
        Kommana Roshitha
      </motion.h1>

      <motion.ul
        className="flex gap-6 text-sm md:text-base relative"
        initial="hidden"
        animate="visible"
        variants={{ hidden: {}, visible: { transition: { staggerChildren: 0.1 } } }}
      >
        {navLinks.map(({ name, icon, to, dropdown }) => (
          <motion.li
            key={name}
            className={`relative flex items-center gap-1 cursor-pointer transition-all duration-200 ${
              activeLink === name ? 'text-primary' : 'text-white hover:text-primary'
            }`}
            variants={{
              hidden: { opacity: 0, y: -10 },
              visible: { opacity: 1, y: 0 },
            }}
            whileHover={{ scale: 1.05 }}
            ref={name === 'Projects' ? dropdownRef : null}
          >
            {!dropdown ? (
              <ScrollLink
                to={to}
                smooth
                duration={500}
                offset={-100}
                onSetActive={handleSetActive}
                className="flex items-center gap-1"
              >
                <span className="text-lg">{icon}</span> {name}
              </ScrollLink>
            ) : (
              <>
                <div
                  onClick={() => setShowDropdown((prev) => !prev)}
                  className="flex items-center gap-1"
                >
                  <span className="text-lg">{icon}</span> {name}
                  {showDropdown ? (
                    <FaChevronUp className="ml-1 text-xs" />
                  ) : (
                    <FaChevronDown className="ml-1 text-xs" />
                  )}
                </div>

                {showDropdown && (
                  <motion.ul
                    className="absolute top-8 left-0 bg-dark border border-gray-700 rounded-lg shadow-lg w-48 text-sm p-2 z-50"
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.2 }}
                  >
                    {dropdown.map((item) => (
                      <a
                        key={item.name}
                        href={`/#projects#${item.to}`}
                        onClick={() => setShowDropdown(false)}
                        className="flex items-center gap-2 p-2 hover:bg-white/10 rounded cursor-pointer text-white hover:text-primary"
                      >
                        <span>{item.icon}</span> {item.name}
                      </a>
                    ))}
                  </motion.ul>
                )}
              </>
            )}
          </motion.li>
        ))}
      </motion.ul>
    </motion.nav>
  );
};

export default Navbar;
