import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const projects = [
  {
    id: 'donateease',
    name: 'DonateEase – Web-Based Donation Platform',
    shortDesc: 'Web app enabling donations across multiple categories with QR-based payments and dynamic forms.',
    imageUrl: '/assets/donate.png', // Replace with your actual project image URL or use a local import
    liveDemo: 'https://donateease-2f624.web.app/',
    github: 'https://github.com/Roshitha-Kommana/DonateEase',
    technologies: ['HTML', 'JavaScript', 'Firebase', 'Cloudinary'],
    keyFeatures: [
      'Built a web application enabling users to donate across 4 categories (money, clothes, books, electronics)',
      'Integrated QR-based UPI payments and Cloudinary API for secure screenshot storage',
      'Enabled conditional dynamic forms with validations and Firestore-based data persistence',
      'Achieved 100% functional donation tracking and improved submission accuracy',
    ],
    date: 'Dec 2024 - Feb 2025',
  },
  {
    id: 'smartocr',
    name: 'Smart OCR – Automated Text Extraction System',
    shortDesc: 'OCR pipeline for scanned PDFs and images with image preprocessing and Tesseract OCR.',
    imageUrl: 'https://raw.githubusercontent.com/Roshitha-Kommana/smart_ocr/main/assets/ocr_sample.png', // Replace with your image or local
    liveDemo: null,
    github: 'https://github.com/Roshitha-Kommana/smart_ocr',
    technologies: ['Python', 'OpenCV', 'PyMuPDF', 'Tesseract'],
    keyFeatures: [
      'Built an OCR pipeline to extract text from scanned PDFs and images',
      'Applied preprocessing (grayscale, denoising, thresholding) to improve accuracy',
      'Used Tesseract to convert images into structured text for document digitization',
    ],
    date: 'N/A',
  },
];

const certificates = [
  // You can add certificate objects here with fields like name, issuer, date, and link if you want
  {
    id: 1,
    title: 'Artificial Intelligence Fundamentals',
    issuer: 'IBM SkillsBuild',
    date: 'Feb 2025',
    link: 'https://www.credly.com/badges/xxxxxxxx', // Replace with your actual certificate URL
  },
  {
    id: 2,
    title: 'Frontend Web Developer',
    issuer: 'Infosys',
    date: 'Mar 2025',
    link: '',
  },
  // Add more certificates as you like
];

const ProjectsCertificates = () => {
  const [activeTab, setActiveTab] = useState('projects');

  return (
    <section className="max-w-7xl mx-auto px-6 py-20 bg-[#0b0f19] text-white select-none">
      <h2 className="text-4xl font-extrabold mb-12 text-center text-purple-400 tracking-wide">
        Projects & Certificates
      </h2>

      {/* Tab buttons */}
      <div className="flex justify-center space-x-6 mb-14">
        {['projects', 'certificates'].map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            className={`relative px-6 py-2 rounded-xl bg-purple-900/30 backdrop-blur-md border border-purple-600 flex items-center space-x-2
              ${
                activeTab === tab
                  ? 'bg-purple-700/80 border-purple-400 shadow-lg shadow-purple-700/40 text-white'
                  : 'text-purple-300 hover:bg-purple-800/40 hover:border-purple-500'
              } transition-all duration-300 ease-in-out font-semibold`}
          >
            {tab === 'projects' ? (
              <>
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="h-5 w-5 text-purple-300"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  strokeWidth={2}
                >
                  <path strokeLinecap="round" strokeLinejoin="round" d="M3 7h18M3 12h18M3 17h18" />
                </svg>
                <span>Projects</span>
              </>
            ) : (
              <>
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="h-5 w-5 text-purple-300"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  strokeWidth={2}
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    d="M9 17v-6a2 2 0 114 0v6m-2-3v3m0-6V7a2 2 0 10-4 0v1"
                  />
                </svg>
                <span>Certificates</span>
              </>
            )}
          </button>
        ))}
      </div>

      {/* Content */}
      {activeTab === 'projects' ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {projects.map((proj) => (
            <div
              key={proj.id}
              className="bg-[#1e2235] rounded-xl shadow-md p-4 flex flex-col items-center cursor-pointer hover:shadow-purple-600 transition-shadow"
            >
              <img
                src={proj.imageUrl}
                alt={proj.name}
                className="w-full h-36 object-contain rounded-md p-3"
              />
              <h3 className="mt-4 text-lg font-semibold text-purple-300 text-center">{proj.name}</h3>
              <p className="mt-2 text-sm text-purple-200 text-center">{proj.shortDesc}</p>

              <div className="mt-4 w-full flex justify-between items-center">
                {proj.liveDemo ? (
                  <a
                    href={proj.liveDemo}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-purple-400 hover:text-purple-600 font-semibold"
                  >
                    Live Demo
                  </a>
                ) : (
                  <span className="text-purple-700 text-sm italic">No Demo</span>
                )}
                <Link
                  to={`/projects/${proj.id}`}
                  className="text-purple-400 hover:text-purple-600 font-semibold"
                >
                  Full Details &rarr;
                </Link>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="space-y-6 max-w-3xl mx-auto">
          {certificates.length ? (
            certificates.map((cert) => (
              <div
                key={cert.id}
                className="bg-[#1e2235] p-4 rounded-xl shadow-md border border-purple-600 flex justify-between items-center"
              >
                <div>
                  <h3 className="text-purple-300 font-semibold text-lg">{cert.title}</h3>
                  <p className="text-purple-400 text-sm">
                    {cert.issuer} — <span className="italic">{cert.date}</span>
                  </p>
                </div>
                {cert.link ? (
                  <a
                    href={cert.link}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-purple-400 hover:text-purple-600 font-semibold"
                  >
                    View Certificate
                  </a>
                ) : (
                  <span className="text-purple-700 italic text-sm">No Link</span>
                )}
              </div>
            ))
          ) : (
            <p className="text-center text-purple-400">No certificates to show.</p>
          )}
        </div>
      )}
    </section>
  );
};

export default ProjectsCertificates;
