import React from 'react';
import { ReactTyped } from 'react-typed';
import { FaGithub, FaLinkedin, FaInstagram } from 'react-icons/fa';
import { motion } from 'framer-motion';

const Hero = () => {
  return (
    <section className="relative text-white min-h-screen flex items-center justify-center px-4 sm:px-6 md:px-8 py-12 overflow-hidden">
      
      {/* Background image (Layer 1) */}
      <div className="absolute top-12 left-4 sm:left-20 opacity-10 sm:opacity-20 z-0">
        <img
          src="/profile.jpg"
          alt="Decorative Profile Background"
          className="w-72 h-72 sm:w-96 sm:h-96 object-cover rounded-full shadow-2xl"
        />
      </div>

      {/* Foreground content (Layer 2) */}
      <div className="relative z-10 max-w-6xl w-full grid grid-cols-1 md:grid-cols-2 gap-12 items-center">
        
        {/* Left side: Typed text + description + buttons + social */}
        <motion.div
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
          className="flex flex-col gap-6 text-center md:text-left"
        >
          <h1 className="text-3xl sm:text-4xl md:text-5xl font-bold leading-tight">
            I am a{' '}
            <span className="text-primary">
              <ReactTyped
                strings={[
                  'Web Developer',
                  'AI & ML Explorer',
                  'Full-Stack Developer',
                  'Software Engineer'
                ]}
                typeSpeed={70}
                backSpeed={40}
                loop
                showCursor={true}
                cursorChar="|"
                smartBackspace={true}
              />
            </span>
          </h1>

          <p className="text-base sm:text-lg text-gray-300 max-w-xl mx-auto md:mx-0">
            Enhancing digital experiences that are smooth, scalable, and made to impress.
          </p>

          <div className="flex flex-col sm:flex-row gap-4 justify-center md:justify-start">
            <a
              href="/resume.pdf"
              target="_blank"
              rel="noopener noreferrer"
              className="bg-primary px-6 py-2 rounded shadow hover:bg-primary-dark transition"
            >
              View Resume
            </a>
            <a
              href="/resume.pdf"
              download
              className="border border-primary px-6 py-2 rounded hover:bg-primary hover:text-white transition"
            >
              Download Resume
            </a>
          </div>

          <div className="flex gap-6 text-2xl sm:text-3xl text-gray-400 justify-center md:justify-start mt-4">
            <a href="https://github.com/Roshitha-Kommana" target="_blank" rel="noopener noreferrer" className="hover:text-primary">
              <FaGithub />
            </a>
            <a href="https://linkedin.com/in/roshitha-kommana" target="_blank" rel="noopener noreferrer" className="hover:text-primary">
              <FaLinkedin />
            </a>
            <a href="https://instagram.com" target="_blank" rel="noopener noreferrer" className="hover:text-primary">
              <FaInstagram />
            </a>
          </div>
        </motion.div>

        {/* Right side: (optional or keep blank if stacking over text) */}
        <motion.div
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
          className="flex justify-center"
        >
          {/* You can keep this empty or use a second decorative element */}
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
