import React from 'react';
import { motion } from 'framer-motion';

const educationData = [
  {
    id: 3,
    institute: 'MVGR College of Engineering',
    course: 'B.Tech in CSE',
    year: '2022 - 2026',
    score: 'CGPA: 8.53',
  },
  {
    id: 2,
    institute: 'Sri Chaitanya Junior College',
    course: 'Intermediate - MPC',
    year: '2020 - 2022',
    score: '74.8%',
  },
  {
    id: 1,
    institute: 'Sri Chaitanya Techno School',
    course: 'SSC',
    year: '2019 - 2020',
    score: '99.8%',
  },
];

const Education = () => {
  return (
    <section id="education" className="relative max-w-6xl mx-auto py-20 px-4 bg-[#0b0f19] text-white">
      <h2 className="text-4xl font-bold mb-20 text-center text-purple-400">Education</h2>

      <div className="relative">
        {/* Vertical Timeline Line */}
        <div className="absolute left-1/2 transform -translate-x-1/2 h-full w-1 bg-purple-500" />

        {/* Timeline Cards */}
        <div className="space-y-10"> {/* Reduced from space-y-20 to space-y-10 */}
          {educationData.map(({ id, institute, course, year, score }, index) => {
            const isLeft = index % 2 === 0;

            return (
              <motion.div
                key={id}
                initial={{ opacity: 0, x: isLeft ? -100 : 100 }}
                whileInView={{ opacity: 1, x: 0 }}
                viewport={{ once: true, amount: 0.5 }}
                transition={{ duration: 0.6, delay: index * 0.2 }}
                className={`flex justify-between items-center w-full relative`}
              >
                {/* Left Side Card */}
                {isLeft ? (
                  <>
                    <div className="w-1/2 pr-8 flex justify-end">
                      <div className="bg-[#111827] max-w-xs w-full p-4 h-40 rounded-lg shadow-md text-center flex flex-col justify-center items-center hover:shadow-purple-400/30">
                        <h3 className="text-base font-semibold text-purple-400 mb-1">{course}</h3>
                        <p className="text-gray-300 mb-1">{institute}</p>
                        <p className="text-gray-400 italic mb-1">{year}</p>
                        <p className="text-white font-medium">{score}</p>
                      </div>
                    </div>
                    {/* Timeline Dot */}
                    <div className="w-0 flex items-center justify-center">
                      <div className="w-4 h-4 bg-purple-500 border-4 border-[purple] rounded-full z-10" />
                    </div>
                    <div className="w-1/2" /> {/* Empty Right */}
                  </>
                ) : (
                  <>
                    <div className="w-1/2" /> {/* Empty Left */}
                    {/* Timeline Dot */}
                    <div className="w-0 flex items-center justify-center">
                      <div className="w-4 h-4 bg-purple-500 border-4 border-[#0b0f19] rounded-full z-10" />
                    </div>
                    <div className="w-1/2 pl-8 flex justify-start">
                      <div className="bg-[#111827] max-w-xs w-full p-4 h-40 rounded-lg shadow-md text-center flex flex-col justify-center items-center hover:shadow-purple-400/30">
                        <h3 className="text-base font-semibold text-purple-400 mb-1">{course}</h3>
                        <p className="text-gray-300 mb-1">{institute}</p>
                        <p className="text-gray-400 italic mb-1">{year}</p>
                        <p className="text-white font-medium">{score}</p>
                      </div>
                    </div>
                  </>
                )}
              </motion.div>
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default Education;
