import React from 'react';
import { motion } from 'framer-motion';

const educationData = [
  {
    id: 3,
    institute: 'MVGR College of Engineering',
    course: 'B.Tech in CSE',
    year: '2022 - 2026',
    score: 'CGPA: 8.53',
  },
  {
    id: 2,
    institute: 'Sri Chaitanya Junior College',
    course: 'Intermediate - MPC',
    year: '2020 - 2022',
    score: '74.8%',
  },
  {
    id: 1,
    institute: 'Sri Chaitanya Techno School',
    course: 'SSC',
    year: '2019 - 2020',
    score: '99.8%',
  },
];

const Education = () => {
  return (
    <section
      id="education"
      className="relative max-w-4xl mx-auto py-20 px-4 bg-[#0b0f19] text-white"
    >
      <h2 className="text-4xl font-bold mb-12 text-center text-purple-400">Education</h2>

      <div className="space-y-8">
        {educationData.map(({ id, institute, course, year, score }, index) => (
          <motion.div
            key={id}
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true, amount: 0.3 }}
            transition={{ duration: 0.6, delay: index * 0.2 }}
            className="bg-[#111827] p-6 rounded-xl shadow-lg hover:shadow-purple-400/30 text-center"
          >
            <h3 className="text-xl font-semibold text-purple-400 mb-1">{course}</h3>
            <p className="text-gray-300 mb-1">{institute}</p>
            <p className="text-gray-400 italic mb-1">{year}</p>
            <p className="text-white font-medium">{score}</p>
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default Education;
