import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import {
  FaHome, FaUser, FaGraduationCap,
  FaProjectDiagram, FaCertificate, FaMedal, FaEnvelope
} from 'react-icons/fa';

const navLinks = [
  { name: 'Home', icon: <FaHome />, href: '#hero' },
  { name: 'About', icon: <FaUser />, href: '#about' },
  { name: 'Education', icon: <FaGraduationCap />, href: '#education' },
  {
    name: 'Projects', icon: <FaProjectDiagram />, href: '#projects', dropdown: [
      { name: 'Projects', href: '#projects' },
      { name: 'Certificates', href: '#certificates', icon: <FaCertificate /> },
      { name: 'Badges', href: '#badges', icon: <FaMedal /> },
    ]
  },
  { name: 'Contact', icon: <FaEnvelope />, href: '#contact' },
];

const sectionMap = {
  '#hero': 'Home',
  '#about': 'About',
  '#education': 'Education',
  '#projects': 'Projects',
  '#certificates': 'Projects',
  '#badges': 'Projects',
  '#contact': 'Contact'
};

const Navbar = () => {
  const [activeLink, setActiveLink] = useState('Home');
  const [showDropdown, setShowDropdown] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const sectionOffsets = Object.keys(sectionMap).map((selector) => {
        const el = document.querySelector(selector);
        return {
          selector,
          offset: el ? el.offsetTop - 120 : 0, // adjust as needed
        };
      });

      const scrollY = window.scrollY;

      for (let i = sectionOffsets.length - 1; i >= 0; i--) {
        if (scrollY >= sectionOffsets[i].offset) {
          setActiveLink(sectionMap[sectionOffsets[i].selector]);
          break;
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Call once on mount
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleClick = (name, href) => {
    setActiveLink(name);
    document.querySelector(href)?.scrollIntoView({ behavior: 'smooth' });
    setShowDropdown(false);
  };

  return (
    <motion.nav
      className="flex justify-between items-center px-4 py-3 text-white fixed top-0 w-full z-50 bg-dark/80 backdrop-blur-md border-b border-gray-700"
      initial={{ y: -60, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.6 }}
    >
      <motion.h1
        className="text-xl font-bold text-primary"
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ delay: 0.4 }}
      >
        Kommana Roshitha
      </motion.h1>

      <motion.ul
        className="flex gap-6 text-sm md:text-base relative"
        initial="hidden"
        animate="visible"
        variants={{
          hidden: {},
          visible: { transition: { staggerChildren: 0.1 } },
        }}
      >
        {navLinks.map(({ name, icon, href, dropdown }) => (
          <motion.li
            key={name}
            className={`relative flex items-center gap-1 cursor-pointer transition-all duration-200 ${
              activeLink === name ? 'text-primary' : 'text-white hover:text-primary'
            }`}
            onMouseEnter={() => dropdown && setShowDropdown(true)}
            onMouseLeave={() => dropdown && setShowDropdown(false)}
            onClick={() => !dropdown && handleClick(name, href)}
            variants={{
              hidden: { opacity: 0, y: -10 },
              visible: { opacity: 1, y: 0 },
            }}
            whileHover={{ scale: 1.05 }}
          >
            <span className="text-lg">{icon}</span>
            {name}

            {/* Dropdown */}
            {dropdown && showDropdown && (
              <motion.ul
                className="absolute top-8 left-0 bg-dark border border-gray-700 rounded-lg shadow-lg w-44 text-sm p-2 z-50"
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.2 }}
              >
                {dropdown.map((item) => (
                  <li
                    key={item.name}
                    onClick={() => handleClick('Projects', item.href)}
                    className="flex items-center gap-2 p-2 hover:bg-primary/10 rounded cursor-pointer text-white hover:text-primary"
                  >
                    <span>{item.icon}</span> {item.name}
                  </li>
                ))}
              </motion.ul>
            )}

            {activeLink === name && (
              <motion.span
                layoutId="underline"
                className="absolute -bottom-1 left-0 right-0 h-0.5 bg-primary rounded-full"
              />
            )}
          </motion.li>
        ))}
      </motion.ul>
    </motion.nav>
  );
};

export default Navbar;
