import React from 'react';
import { motion } from 'framer-motion';
import { DiCode } from 'react-icons/di';
import { FaDatabase } from 'react-icons/fa';
import { FiTool } from 'react-icons/fi';
import { MdLightbulb } from 'react-icons/md';
import { GiSkills } from 'react-icons/gi';

const skillsData = [
  {
    category: 'Languages',
    icon: <DiCode size={22} />,
    skills: ['Java', 'Python', 'C++', 'C', 'HTML', 'CSS', 'JavaScript', 'Tailwind CSS', 'React.js'],
  },
  {
    category: 'Backend & Databases',
    icon: <FaDatabase size={22} />,
    skills: ['Firebase', 'MongoDB', 'MySQL'],
  },
  {
    category: 'Tools & Libraries',
    icon: <FiTool size={22} />,
    skills: ['OpenCV', 'Tesseract OCR', 'Git', 'VS Code'],
  },
  {
    category: 'Concepts',
    icon: <MdLightbulb size={22} />,
    skills: ['OOP', 'DBMS', 'Data Structures', 'Machine Learning', 'Big Data', 'UI/UX Principles'],
  },
  {
    category: 'Key Strengths',
    icon: <GiSkills size={22} />,
    skills: ['Problem Solving', 'Team Collaboration', 'Active Listener', 'Time Management'],
  },
];

const Skills = () => {
  return (
    <section id="skills" className="text-gray-900 px-6 py-16 bg-[#ECEBE4]">
      <motion.h2
        className="text-3xl font-bold mb-10 text-center text-primary"
        initial={{ opacity: 0, y: -20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        viewport={{ once: false, amount: 0.3 }}
      >
        Skills
      </motion.h2>

      <motion.div
        className="max-w-6xl mx-auto grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6"
        initial={{ opacity: 0, scale: 0.95 }}
        whileInView={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.8, ease: 'easeOut' }}
        viewport={{ once: false, amount: 0.3 }}
      >
        {skillsData.map((category, idx) => (
          <motion.div
            key={idx}
            className="rounded-xl bg-white/20 backdrop-blur-lg shadow-xl border border-white/30 p-6 hover:shadow-2xl transition-all duration-300"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: idx * 0.15 }}
            viewport={{ once: false, amount: 0.3 }}
          >
            {/* Icon & Category Title */}
            <div className="flex items-center gap-3 mb-4 text-primary">
              <div className="p-2 bg-white/30 backdrop-blur-sm rounded-full shadow-md">
                {category.icon}
              </div>
              <h3 className="text-lg font-semibold">{category.category}</h3>
            </div>

            {/* Skills as badges */}
            <div className="flex flex-wrap gap-2 mt-3">
              {category.skills.map((skill, i) => (
                <span
                  key={i}
                  className="text-sm px-3 py-1 bg-white/30 text-gray-800 rounded-full backdrop-blur-md border border-white/20 shadow-sm"
                >
                  {skill}
                </span>
              ))}
            </div>
          </motion.div>
        ))}
      </motion.div>
    </section>
  );
};

export default Skills;
