import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const projects = [
  {
    title: 'DonateEase Platform',
    description: 'A user-friendly platform connecting donors with NGOs.',
    link: '#',
  },
  {
    title: 'Smart OCR',
    description: 'Automated text extraction from scanned documents using AI.',
    link: '#',
  },
  // Add more projects here...
];

const certificates = [
  {
    title: 'Artificial Intelligence Fundamentals',
    issuer: 'IBM SkillsBuild',
    date: 'Feb 2025',
    link: '#',
  },
  {
    title: 'AWS Academy Cloud Foundations',
    issuer: 'Amazon Web Services',
    date: 'Dec 2024',
    link: '#',
  },
  // Add more certificates here...
];

const tabButtonStyle = (active) =>
  `px-6 py-2 rounded-t-md font-semibold ${
    active ? 'bg-purple-600 text-white' : 'bg-gray-700 text-gray-300 hover:bg-purple-600 hover:text-white'
  } transition-colors duration-300`;

const ProjectsCertificates = () => {
  const [activeTab, setActiveTab] = useState('projects');

  return (
    <section className="max-w-6xl mx-auto py-16 px-6 bg-[#0b0f19] text-white select-none">
      <h2 className="text-4xl font-extrabold mb-10 text-center text-purple-400 tracking-wide">
        Projects & Certificates
      </h2>

      {/* Tabs */}
      <div className="flex justify-center mb-8">
        <button
          className={tabButtonStyle(activeTab === 'projects')}
          onClick={() => setActiveTab('projects')}
          aria-label="View Projects"
        >
          Projects
        </button>
        <button
          className={tabButtonStyle(activeTab === 'certificates')}
          onClick={() => setActiveTab('certificates')}
          aria-label="View Certificates"
        >
          Certificates
        </button>
      </div>

      {/* Content */}
      <AnimatePresence mode="wait">
        {activeTab === 'projects' ? (
          <motion.div
            key="projects"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            transition={{ duration: 0.3 }}
            className="grid grid-cols-1 md:grid-cols-2 gap-8"
          >
            {projects.map(({ title, description, link }) => (
              <div
                key={title}
                className="bg-[#1e2235] p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow cursor-pointer"
                onClick={() => window.open(link, '_blank')}
                role="button"
                tabIndex={0}
                onKeyDown={(e) => {
                  if (e.key === 'Enter') window.open(link, '_blank');
                }}
              >
                <h3 className="text-xl font-semibold text-purple-300 mb-2">{title}</h3>
                <p className="text-gray-300">{description}</p>
              </div>
            ))}
          </motion.div>
        ) : (
          <motion.div
            key="certificates"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            transition={{ duration: 0.3 }}
            className="grid grid-cols-1 md:grid-cols-2 gap-8"
          >
            {certificates.map(({ title, issuer, date, link }) => (
              <div
                key={title}
                className="bg-[#1e2235] p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow cursor-pointer"
                onClick={() => window.open(link, '_blank')}
                role="button"
                tabIndex={0}
                onKeyDown={(e) => {
                  if (e.key === 'Enter') window.open(link, '_blank');
                }}
              >
                <h3 className="text-xl font-semibold text-purple-300 mb-1">{title}</h3>
                <p className="text-gray-400 italic mb-1">{issuer}</p>
                <p className="text-gray-400 text-sm">{date}</p>
              </div>
            ))}
          </motion.div>
        )}
      </AnimatePresence>
    </section>
  );
};

export default ProjectsCertificates;
